CREATE OR REPLACE PROCEDURE transferir_departamento_medico ( nr_atendimento_p bigint, cd_depart_destino_p bigint) AS $body$
DECLARE

 
nr_sequencia_w bigint; -- atend_paciente_unidade
cd_departamento_w bigint;
dt_entrada_unidade_w timestamp;

BEGIN
select  max(dt_entrada_unidade)
into STRICT dt_entrada_unidade_w
from  atend_paciente_unidade
where nr_atendimento = nr_atendimento_p;

if (dt_entrada_unidade_w IS NOT NULL AND dt_entrada_unidade_w::text <> '') then
 select  nr_seq_interno
 into STRICT nr_sequencia_w
 from  atend_paciente_unidade
 where nr_atendimento = nr_atendimento_p
 and dt_entrada_unidade   = dt_entrada_unidade_w;
 -- 
 select  cd_departamento
 into STRICT cd_departamento_w
 from  atend_paciente_unidade
 where nr_atendimento = nr_atendimento_p
 and dt_entrada_unidade   = dt_entrada_unidade_w;

 
 update atend_paciente_unidade
 set cd_departamento = cd_depart_destino_p
 where nr_atendimento = nr_atendimento_p
 and dt_entrada_unidade   = dt_entrada_unidade_w;

 
end if;




INSERT
INTO TRANSF_DEPARTAMENTO_MEDICO(
    NR_SEQUENCIA,
    DT_ATUALIZACAO,
    NM_USUARIO,
    DT_ATUALIZACAO_NREC,
    NM_USUARIO_NREC,
    DT_TRANSFERENCIA,
    NR_ATENDIMENTO,
    NR_ATEND_PACIENTE_UNIDADE,
    CD_DEPART_DESTINO,
    CD_DEPART_ORIGEM
  )
  VALUES (
    nextval('transf_departamento_medico_seq'),
    clock_timestamp(),
    wheb_usuario_pck.get_nm_usuario,
    clock_timestamp(),
    wheb_usuario_pck.get_nm_usuario,
    clock_timestamp(),
    nr_atendimento_p,
    nr_sequencia_w, 
    cd_depart_destino_p, 
    cd_departamento_w
  );
CALL generate_soap_patient_transfer(nr_seq_interno_p	=> nr_sequencia_w,
                               nr_atendimento_p => nr_atendimento_p,
                               cd_pessoa_fisica_p => obter_pessoa_atendimento(nr_atendimento_p,'C'));
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
