CREATE OR REPLACE PROCEDURE transferir_dietas_paciente (dt_transferencia_p timestamp, nr_atendimento_p bigint, cd_setor_transf_p bigint, cd_unid_basica_p text, cd_unid_compl_p text, nm_usuario_p text) AS $body$
DECLARE

				     
nr_sequencia_w		bigint;	
qt_min_transf_w		bigint;	

C01 CURSOR FOR
SELECT	nr_sequencia
from	mapa_dieta
where	ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_dieta) >= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(clock_timestamp())
and	nr_atendimento = nr_atendimento_p
and	Obter_data_dieta_mapa(dt_dieta,cd_dieta,cd_refeicao)+coalesce(qt_min_transf_w,0)/1440 > dt_transferencia_p
and	(Obter_data_dieta_mapa(dt_dieta,cd_dieta,cd_refeicao) IS NOT NULL AND (Obter_data_dieta_mapa(dt_dieta,cd_dieta,cd_refeicao))::text <> '');


BEGIN
qt_min_transf_w := Obter_Param_Usuario(1000, 135, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, qt_min_transf_w);

open c01;
loop
	fetch c01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		
	update	mapa_dieta
	set	cd_setor_atendimento	= cd_setor_transf_p,
		cd_unidade_basica	= cd_unid_basica_p,
		cd_unidade_compl	= cd_unid_compl_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia 		= nr_sequencia_w;


end loop;
close C01;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
