SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE transferir_dietas_servico (dt_transferencia_p timestamp, nr_atendimento_p bigint, cd_setor_transf_p bigint, nm_usuario_p text) AS $body$
DECLARE

				     
nr_sequencia_w		bigint;
qt_registro_w		bigint;				

C01 CURSOR FOR
SELECT	nr_sequencia
from	nut_atend_serv_dia
where	ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_servico) >= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(clock_timestamp())
and	nr_atendimento = nr_atendimento_p
and	dt_servico > dt_transferencia_p;


BEGIN

begin
select	1
into STRICT	qt_registro_w
from	nut_atend_serv_dia
where	nr_atendimento = nr_atendimento_p  LIMIT 1;
exception
when others then
	qt_registro_w := 0;
end;

if (qt_registro_w > 0) then
	open c01;
	loop
	fetch c01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		
		update	nut_atend_serv_dia
		set	cd_setor_atendimento	= cd_setor_transf_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_sequencia 		= nr_sequencia_w;
		
		
	end loop;
	close C01;
end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
