SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE transferir_itens_ordem ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, nm_usuario_p text, nr_ordem_compra_nova_p INOUT bigint) AS $body$
DECLARE


qt_existe_w			integer;
dt_liberacao_w			timestamp;
dt_baixa_w			timestamp;
nr_seq_motivo_cancel_w		bigint;
nr_ordem_compra_w		bigint;
nr_item_oci_w			integer;
nr_seq_entrega_w			bigint;
consiste_contrato_w       bigint;


BEGIN

select count(*)
into STRICT consiste_contrato_w
from  contrato_regra_nf
where nr_ordem_compra = nr_ordem_compra_p
and nr_item_oci = nr_item_oci_p;

if (consiste_contrato_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1058469, 'NR_ITEM_OCI_P='||nr_item_oci_p);
end if;


if (coalesce(nr_ordem_compra_nova_p,0) > 0) then
	select	count(*)
	into STRICT	qt_existe_w
	from	ordem_compra
	where	nr_ordem_compra = nr_ordem_compra_nova_p;
	if (qt_existe_w = 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266349,'NR_ORDEM_COMPRA=' || nr_ordem_compra_nova_p);
		--'Não existe ordem de compra com o número = ' || nr_ordem_compra_nova_p);
	end if;

	select	dt_liberacao,
		dt_baixa,
		nr_seq_motivo_cancel
	into STRICT	dt_liberacao_w,
		dt_baixa_w,
		nr_seq_motivo_cancel_w
	from	ordem_compra
	where	nr_ordem_compra = nr_ordem_compra_nova_p;

	if (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266350);
		--Esta ordem de compra já está liberada');
	end if;

	if (nr_seq_motivo_cancel_w > 0) or (dt_baixa_w IS NOT NULL AND dt_baixa_w::text <> '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266351);
		--'Esta ordem de compra está baixada ou cancelada');
	end if;
end if;

nr_ordem_compra_w		:= nr_ordem_compra_nova_p;

if (coalesce(nr_ordem_compra_nova_p,0) = 0) then

	select	nextval('ordem_compra_seq')
	into STRICT	nr_ordem_compra_w
	;

	nr_ordem_compra_nova_p	:= nr_ordem_compra_w;

	insert into ordem_compra(
		nr_ordem_compra,			cd_estabelecimento,		cd_cgc_fornecedor,
		cd_condicao_pagamento,		cd_comprador,			dt_ordem_compra,
		dt_atualizacao,			nm_usuario,			cd_moeda,
		ie_situacao,			dt_inclusao,			cd_pessoa_solicitante,
		cd_cgc_transportador,		ie_frete,				vl_frete,
		pr_multa_atraso,			pr_desconto,			pr_desc_pgto_antec,
		pr_juros_negociado,		cd_estab_transf,			ds_pessoa_contato,
		ds_observacao,			cd_motivo_alteracao,		cd_local_entrega,
		dt_entrega,			ie_tipo_ordem,			ie_aviso_chegada,
		nr_requisicao,			cd_pessoa_fisica,			nr_ordem_agrup,
		ie_emite_obs,			ie_urgente,			nr_seq_interno,
		nr_atendimento,			nm_usuario_imp,			ie_somente_pagto,
		vl_despesa_acessoria,		cd_setor_atendimento,		nr_seq_subgrupo_compra,
		pr_desc_financeiro,			dt_faturamento,			cd_convenio,
		nm_usuario_aceite,			nr_seq_forma_pagto,		nr_documento_externo,
		vl_desconto)
	SELECT	nr_ordem_compra_w,		cd_estabelecimento,		cd_cgc_fornecedor,
		cd_condicao_pagamento,		cd_comprador,			clock_timestamp(),
		clock_timestamp(),				nm_usuario_p,			cd_moeda,
		ie_situacao,			dt_inclusao,			cd_pessoa_solicitante,
		cd_cgc_transportador,		ie_frete,				vl_frete,
		pr_multa_atraso,			pr_desconto,			pr_desc_pgto_antec,
		pr_juros_negociado,		cd_estab_transf,			ds_pessoa_contato,
		ds_observacao,			cd_motivo_alteracao,		cd_local_entrega,
		dt_entrega,			'A',				ie_aviso_chegada,
		nr_requisicao,			cd_pessoa_fisica,			nr_ordem_agrup,
		ie_emite_obs,			ie_urgente,			nr_seq_interno,
		nr_atendimento,			nm_usuario_imp,			ie_somente_pagto,
		vl_despesa_acessoria,		cd_setor_atendimento,		nr_seq_subgrupo_compra,
		pr_desc_financeiro,			dt_faturamento,			cd_convenio,
		nm_usuario_aceite,			nr_seq_forma_pagto,		nr_documento_externo,
		vl_desconto
	from	ordem_compra
	where	nr_ordem_compra = nr_ordem_compra_p;
end if;

select	coalesce(max(nr_item_oci), 0) + 1
into STRICT	nr_item_oci_w
from	ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_nova_p;


insert into ordem_compra_item(
	nr_ordem_compra,			nr_item_oci,			cd_material,
	cd_unidade_medida_compra,		vl_unitario_material,			qt_material,
	dt_atualizacao,			nm_usuario,			ie_situacao,
	cd_pessoa_solicitante,		pr_descontos,			cd_local_estoque,
	ds_material_direto,			ds_observacao,			cd_motivo_alteracao,
	nr_cot_compra,			nr_item_cot_compra,		ds_marca,
	vl_item_liquido,			cd_centro_custo,			cd_conta_contabil,
	nr_solic_compra,			nr_item_solic_compra,		qt_conv_unid_fornec,
	ie_geracao_solic,			nr_seq_proj_rec,			pr_desc_financ,
	nr_seq_lic_item,			nr_seq_conta_financ,		qt_original,
	dt_validade,			nr_seq_marca,			nr_seq_ordem_serv,
	vl_ultima_compra,			vl_dif_ultima_compra,		pr_dif_ultima_compra,
	nr_seq_unidade_adic,		nr_seq_criterio_rateio,		nr_serie_material,
	nr_solic_compra_cancel,		vl_desconto,			nr_seq_lote_fornec,
	nr_seq_proj_gpi,			nr_seq_etapa_gpi,			nr_seq_conta_gpi,
	nr_contrato,				vl_total_item)
SELECT	nr_ordem_compra_w,		nr_item_oci_w,			cd_material,
	cd_unidade_medida_compra,		vl_unitario_material,			qt_material,
	dt_atualizacao,			nm_usuario_p,			ie_situacao,
	cd_pessoa_solicitante,		pr_descontos,			cd_local_estoque,
	ds_material_direto,			ds_observacao,			cd_motivo_alteracao,
	nr_cot_compra,			nr_item_cot_compra,		ds_marca,
	vl_item_liquido,			cd_centro_custo,			cd_conta_contabil,
	nr_solic_compra,			nr_item_solic_compra,		qt_conv_unid_fornec,
	ie_geracao_solic,			nr_seq_proj_rec,			pr_desc_financ,
	nr_seq_lic_item,			nr_seq_conta_financ,		qt_material,
	dt_validade,			nr_seq_marca,			nr_seq_ordem_serv,
	vl_ultima_compra,			vl_dif_ultima_compra,		pr_dif_ultima_compra,
	nr_seq_unidade_adic,		nr_seq_criterio_rateio,		nr_serie_material,
	nr_solic_compra_cancel,		vl_desconto,			nr_seq_lote_fornec,
	nr_seq_proj_gpi,			nr_seq_etapa_gpi,			nr_seq_conta_gpi,
	nr_contrato,				(vl_unitario_material * qt_material)
from	ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_p
and	nr_item_oci = nr_item_oci_p;

select	nextval('ordem_compra_item_entrega_seq')
into STRICT	nr_seq_entrega_w
;

insert into ordem_compra_item_entrega(
	nr_sequencia,
	nr_ordem_compra,
	nr_item_oci,
	dt_prevista_entrega,
	qt_prevista_entrega,
	dt_atualizacao,
	nm_usuario,
	ds_observacao,
	ie_status_exportar,
	dt_entrega_original,
	dt_entrega_limite)
SELECT	nr_seq_entrega_w,
	nr_ordem_compra_w,
	nr_item_oci_w,
	dt_prevista_entrega,
	qt_prevista_entrega,
	clock_timestamp(),
	nm_usuario_p,
	ds_observacao,
	ie_status_exportar,
	dt_entrega_original,
	dt_entrega_limite
from	ordem_compra_item_entrega
where	nr_ordem_compra = nr_ordem_compra_p
and	nr_item_oci = nr_item_oci_p;

insert into ordem_compra_item_trib(
	nr_ordem_compra,
	nr_item_oci,
	cd_tributo,
	pr_tributo,
	vl_tributo,
	dt_atualizacao,
	nm_usuario,
	ds_observacao,
	dt_atualizacao_nrec,
	nm_usuario_nrec)
SELECT	nr_ordem_compra_w,
	nr_item_oci_w,
	cd_tributo,
	pr_tributo,
	vl_tributo,
	clock_timestamp(),
	nm_usuario_p,
	ds_observacao,
	clock_timestamp(),
	nm_usuario_p
from	ordem_compra_item_trib
where	nr_ordem_compra = nr_ordem_compra_p
and	nr_item_oci = nr_item_oci_p;

CALL gerar_ordem_compra_venc(nr_ordem_compra_w,nm_usuario_p);

delete
from	ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_p
and	nr_item_oci = nr_item_oci_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE transferir_itens_ordem ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, nm_usuario_p text, nr_ordem_compra_nova_p INOUT bigint) FROM PUBLIC;

