CREATE OR REPLACE PROCEDURE transferir_saldo_proj_rec ( nr_seq_proj_saida_p bigint, nr_seq_proj_entrada_p bigint, vl_transferido_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_saida_w			bigint;


BEGIN

select	nextval('proj_recurso_trans_saldo_seq')
into STRICT	nr_seq_saida_w
;

/*Gravar a sa√≠da de saldo de um projeto*/

insert into proj_recurso_trans_saldo(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_proj_rec,
	ie_entrada_saida,
	dt_transferencia,
	vl_transferido,
	nr_seq_proj_ref)
values (	nr_seq_saida_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_proj_saida_p,
	'S',
	trunc(clock_timestamp(),'dd'),
	vl_transferido_p,
	nr_seq_proj_entrada_p); /*Fica gravado pra onde foi transferido*/
/*Gravar a entrada de saldo do projeto*/

insert into proj_recurso_trans_saldo(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_proj_rec,
	ie_entrada_saida,
	dt_transferencia,
	vl_transferido,
	nr_seq_saida_ref,
	nr_seq_proj_ref)
values (	nextval('proj_recurso_trans_saldo_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_proj_entrada_p,
	'E',
	trunc(clock_timestamp(),'dd'),
	vl_transferido_p,
	nr_seq_saida_w,
	nr_seq_proj_saida_p); /*Fica gravado de onde veio o valor transferido*/
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
