CREATE OR REPLACE PROCEDURE transferir_terc_conta_mes (cd_estabelecimento_p bigint, vl_maximo_p bigint, dt_mes_ref_origem_p timestamp, dt_mes_ref_destino_p timestamp, nm_usuario_p text) AS $body$
BEGIN

update	terceiro_conta a
set	a.dt_mesano_referencia	= trunc(dt_mes_ref_destino_p, 'month'),
	a.dt_atualizacao		= clock_timestamp(),
	a.nm_usuario		= nm_usuario_p
where	a.cd_estabelecimento	= cd_estabelecimento_p
and	trunc(a.dt_mesano_referencia, 'month') = trunc(dt_mes_ref_origem_p, 'month')
and	a.ie_status_conta		= 'P'
and (SELECT	coalesce(sum(y.vl_operacao), 0)
	from	terceiro_operacao y
	where	y.nr_seq_conta	= a.nr_sequencia) <= vl_maximo_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
