SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE transferir_titulo_receber ( nr_titulo_p bigint, ie_commit_p text, dt_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
vl_saldo_titulo_w		titulo_receber.vl_saldo_titulo%type;
ds_observacao_w			alteracao_valor.ds_observacao%type;
nr_sequencia_w			alteracao_valor.nr_sequencia%type;	
cd_moeda_w				titulo_receber.cd_moeda%type;	
cd_estabelecimento_w	titulo_receber.cd_estabelecimento%type;	
nr_seq_tf_alt_vl_canc_w	parametro_contas_receber.nr_seq_trans_fin_alt_vl_canc%type;										
									 

BEGIN 
 
if ( coalesce(nr_titulo_p,0) <> 0) then 
 
	select	max(a.vl_saldo_titulo), 
			max(a.cd_moeda), 
			max(a.cd_estabelecimento) 
	into STRICT	vl_saldo_titulo_w, 
			cd_moeda_w, 
			cd_estabelecimento_w 
	from	titulo_receber a 
	where	a.nr_titulo = nr_titulo_p;
	 
	select	max(a.nr_seq_trans_fin_alt_vl_canc) 
	into STRICT	nr_seq_tf_alt_vl_canc_w 
	from	parametro_contas_receber a 
	where	a.cd_estabelecimento	= cd_estabelecimento_w;
	 
	if (coalesce(vl_saldo_titulo_w,0) > 0) then 
	 
		begin	 
			ds_observacao_w := substr(obter_desc_expressao(743466),1,255);	
		exception when others then 
			ds_observacao_w := '';
		end;	
		 
		select	coalesce(max(nr_sequencia),0) + 1 
		into STRICT	nr_sequencia_w 
		from	alteracao_valor 
		where	nr_titulo	= nr_titulo_p;
			 
		insert into alteracao_valor(	nr_titulo, 
										ds_observacao, 
										nr_sequencia, 
										dt_alteracao, 
										vl_anterior, 
										vl_alteracao, 
										cd_motivo, 
										cd_moeda, 
										ie_aumenta_diminui, 
										dt_atualizacao, 
										nm_usuario, 
										nr_lote_contabil, 
										nr_seq_trans_fin) 
							values (	nr_titulo_p, 
										ds_observacao_w, 
										nr_sequencia_w, 
										coalesce(dt_referencia_p,clock_timestamp()), 
										vl_saldo_titulo_w, 
										vl_saldo_titulo_w, 
										3, -- Esse 3 é fixo conforme ocorre na rotina cancealr_titulo_receber. Esse motivo padrão 3 vai na versão. 
										cd_moeda_w, 
										'D', 
										clock_timestamp(), 
										nm_usuario_p, 
										null, 
										nr_seq_tf_alt_vl_canc_w);
		 
		CALL atualizar_saldo_tit_rec( nr_titulo_p, nm_usuario_p);
		 
		update 	titulo_receber a 
		set		a.ie_situacao		= '5' 
		where	a.nr_titulo 		= nr_titulo_p 
		and		a.vl_saldo_titulo 	= 0;
	 
	end if;
 
end if;
 
/*Por padrao o ie_commit_p é Não, pois essa rotina foi criada para ser chamada dentro da unificar_titulos_receber que ja possui commit. */
 
if (coalesce(ie_commit_p,'N') = 'S') then 
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE transferir_titulo_receber ( nr_titulo_p bigint, ie_commit_p text, dt_referencia_p timestamp, nm_usuario_p text) FROM PUBLIC;

