SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE transf_agendamentos_agexame (ds_lista_p text, cd_agenda_p bigint, dt_agenda_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


ds_lista_w			varchar(4000);
tam_lista_w			bigint;
ie_pos_virgula_w		smallint;
nr_seq_agendamento_w		agenda_paciente.nr_sequencia%type;

nr_seq_livre_w			bigint;
nr_seq_encaixe_w		bigint;
hr_agenda_w			timestamp;
dt_agenda_w			timestamp;
nr_minuto_duracao_w		bigint;
ds_retorno_w		varchar(255);
				

BEGIN

ds_lista_w 	:= ds_lista_p;
ds_erro_p	:= '';

while(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '')  loop
	begin
	tam_lista_w		:= length(ds_lista_w);
	ie_pos_virgula_w	:= position(',' in ds_lista_w);

	if (ie_pos_virgula_w <> 0) then
		nr_seq_agendamento_w	:= (substr(ds_lista_w,1,(ie_pos_virgula_w - 1)))::numeric;
		ds_lista_w		:= substr(ds_lista_w,(ie_pos_virgula_w + 1),tam_lista_w);
	end if;
	
	CALL gerar_horario_agenda_exame(
			cd_estabelecimento_p,
			cd_agenda_p,
			dt_agenda_p,
			nm_usuario_p);		
	
	select	max(hr_inicio),
		max(hr_inicio),
		max(nr_minuto_duracao)
	into STRICT	dt_agenda_w,
		hr_agenda_w,
		nr_minuto_duracao_w
	from	agenda_paciente
	where	nr_sequencia = nr_seq_agendamento_w;

	
	select	max(nr_sequencia)
	into STRICT	nr_seq_livre_w
	from	agenda_paciente
	where	cd_agenda = cd_agenda_p
	and	hr_inicio = to_date(to_char(dt_agenda_p,'dd/mm/yyyy') || ' ' || coalesce(to_char(dt_agenda_w,'hh24:mi:ss'),'00:00:00'),'dd/mm/yyyy hh24:mi:ss')
	and	ie_status_agenda = 'L';
	
	if (nr_seq_livre_w > 0) then
		ds_retorno_w := copiar_transferir_agenda_exame(
					cd_estabelecimento_p, 'N', nr_seq_agendamento_w, nr_seq_livre_w, 'T', null, null, nm_usuario_p, ds_retorno_w);
	else
		ds_erro_p := ds_erro_p || to_char(hr_agenda_w,'hh24:mi') ||',';		
	end if;

	end;
	end loop;
	
commit;

ds_erro_p	:= substr(ds_erro_p,1,length(ds_erro_p)-1);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
