CREATE OR REPLACE PROCEDURE transf_autorizacao_conv_eup ( nr_atendimento_p bigint, nr_seq_autorizacao_p bigint, cd_pessoa_fisica_p text, ie_atualiza_guia_conta_p text) AS $body$
DECLARE


cd_convenio_w			bigint;
cd_autorizacao_w			numeric(20);
nr_seq_paciente_w			bigint;


BEGIN

select	cd_convenio,
	cd_autorizacao
into STRICT	cd_convenio_w,
	cd_autorizacao_w
from	autorizacao_convenio
where	nr_sequencia = nr_seq_autorizacao_p;



update	autorizacao_convenio
set   	nr_atendimento     = NULL,
	cd_pessoa_fisica  = cd_pessoa_fisica_p
where 	nr_sequencia      = nr_seq_autorizacao_p;

commit;

update	autorizacao_convenio
set   	nr_atendimento	= nr_atendimento_p
where 	nr_sequencia 	= nr_seq_autorizacao_p;


select	coalesce(max(nr_seq_paciente),0)
into STRICT	nr_seq_paciente_w
from	autorizacao_convenio
where	nr_sequencia	= nr_seq_autorizacao_p;

if (nr_seq_paciente_w <> 0) then
	begin
	update	paciente_atendimento
	set   	nr_atendimento		= nr_atendimento_p
	where 	nr_seq_atendimento	= nr_seq_paciente_w;
	end;
end if;

if (ie_atualiza_guia_conta_p = 'S') then
	begin
	CALL atualizar_guia_conta_autor(nr_atendimento_p,cd_convenio_w,cd_autorizacao_w);
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
