CREATE OR REPLACE PROCEDURE transf_estoque_gerar_entregas (nr_ordem_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nr_item_oci_w				integer;
qt_material_w				double precision;
dt_entrega_w				timestamp;

c01 CURSOR FOR 
SELECT	a.nr_item_oci, 
	a.qt_material, 
	b.dt_entrega 
from	ordem_compra_item a, 
	ordem_compra b 
where	a.nr_ordem_compra = b.nr_ordem_compra 
and	b.nr_ordem_compra = nr_ordem_compra_p;


BEGIN 
 
open c01;
loop 
fetch c01 into 
	nr_item_oci_w, 
	qt_material_w, 
	dt_entrega_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	 
	insert into ordem_compra_item_entrega(nr_sequencia, nr_item_oci, nr_ordem_compra, qt_prevista_entrega, 
							dt_atualizacao, nm_usuario, dt_prevista_entrega) 
	values (nextval('ordem_compra_item_entrega_seq'), nr_item_oci_w, nr_ordem_compra_p, qt_material_w, 
							clock_timestamp(), nm_usuario_p, dt_entrega_w);
 
	end;
end loop;
close C01;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
