CREATE OR REPLACE PROCEDURE transf_exame_setor ( nr_prontuario_p bigint, cd_setor_destino_p bigint, cd_estabelecimento_p bigint, cd_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w	bigint;
nr_sequencia_w	bigint;
nr_seq_solic_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	exame_solicitacao
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	cd_setor_atendimento	= cd_setor_destino_p
	and	nr_prontuario		= nr_prontuario_p
	and	trunc(dt_desejada)	= trunc(clock_timestamp())
	and	ie_status_solic		= 'A'
	and	((cd_agenda		= cd_agenda_p)
	or (coalesce(cd_agenda_p::text, '') = ''))
	order by coalesce(cd_agenda,0) DESC,
	         dt_desejada;


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	nr_seq_solic_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
END LOOP;
CLOSE C01;

if	not(coalesce(nr_seq_solic_w,0) > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(282115);
end if;

select	count(*)
into STRICT	qt_existe_w
from	exame_emprestimo
where	nr_prontuario		= nr_prontuario_p
and	cd_estabelecimento	= cd_estabelecimento_p
and	coalesce(dt_devolucao::text, '') = '';

if (qt_existe_w > 0) then

	update	exame_emprestimo
	set	dt_devolucao		= clock_timestamp(),
		nm_usuario_dev		= nm_usuario_p
	where	nr_prontuario		= nr_prontuario_p
	and	cd_estabelecimento	= cd_estabelecimento_p
	and	coalesce(dt_devolucao::text, '') = '';

	select	nextval('exame_emprestimo_seq')
	into STRICT	nr_sequencia_w
	;

	insert into exame_emprestimo(
		nr_sequencia,
		cd_estabelecimento,
		nr_prontuario,
		dt_atualizacao,
		nm_usuario,
		cd_setor_atendimento,
		dt_emprestimo,
		nm_usuario_empr,
		ie_operacao,
		cd_agenda,
		nr_seq_solic)
	values (	nr_sequencia_w,
		cd_estabelecimento_p,
		nr_prontuario_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_setor_destino_p,
		clock_timestamp(),
		nm_usuario_p,
		'T',
		cd_agenda_p,
		nr_seq_solic_w);

	update	exame_solicitacao
	set	ie_status_solic = 'T'
	where	nr_sequencia	= nr_seq_solic_w;
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(282117);
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
