SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE transf_prescr_apae_agenda ( nr_seq_copia_p bigint, nr_seq_destino_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

						
nr_sequencia_ww		bigint;
nr_sequencia_w		bigint;
nr_prescricao_w		bigint;
nr_nova_prescricao_w	bigint;
dt_agenda_w		timestamp;
cd_pessoa_fisica_w	varchar(14);
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
cd_medico_w		varchar(14);	
nr_atendimento_w	bigint;
hr_inicio_w		timestamp;
ds_erro_w		varchar(2000);
eh_medico_w		varchar(1);
cd_medic_posicionar_w	bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	aval_pre_anestesica
where	nr_seq_agenda 		= nr_seq_copia_p
and	coalesce(ie_situacao,'A')	= 'A';
if (coalesce(nr_sequencia_w::text, '') = '') then
	select	max(ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_agenda)),
		max(cd_pessoa_fisica),
		max(cd_procedimento),
		max(ie_origem_proced)
	into STRICT	dt_agenda_w,
		cd_pessoa_fisica_w,
		cd_procedimento_w,
		ie_origem_proced_w
	from	agenda_paciente
	where	nr_sequencia = nr_seq_copia_p;
	
	if (dt_agenda_w IS NOT NULL AND dt_agenda_w::text <> '') then
		select	max(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	aval_pre_anestesica
		where	ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_avaliacao)	= dt_agenda_w
		and	cd_pessoa_fisica	= cd_pessoa_fisica_w
		and	cd_procedimento		= cd_procedimento_w
		and	ie_origem_proced	= ie_origem_proced_w
		and	coalesce(ie_situacao,'A')	= 'A';
	end if;
end if;	

if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
	select	max(nr_prescricao),
		max(nr_sequencia)
	into STRICT	nr_prescricao_w,
		nr_sequencia_ww
	from	conclusao_recom_apae
	where	nr_seq_aval_pre = nr_sequencia_w;
	if (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then
		select	max(cd_medico)
		into STRICT	cd_medico_w
		from 	prescr_medica
		where 	nr_prescricao = nr_prescricao_w;
		
		select	max(hr_inicio)
		into STRICT	hr_inicio_w
		from	agenda_paciente
		where	nr_sequencia = nr_seq_destino_p;
		
		select 	cd_medico,
			obter_se_medico(cd_medico, 'M'),
			nr_atendimento
		into STRICT	cd_medico_w,
			eh_medico_w,
			nr_atendimento_w
		from	prescr_medica
		where	nr_prescricao	= nr_prescricao_w;
		SELECT * FROM Copia_prescricao(	nr_prescricao_w, nr_atendimento_w, nm_usuario_p, cd_medico_w, hr_inicio_w, 'S', 'S', 'S', 0, 'S', null, substr(hr_inicio_w,12,5), hr_inicio_w, null, 'N', null, null, 24, obter_perfil_ativo, nr_nova_prescricao_w, null, null, null, 'N', cd_medic_posicionar_w) INTO STRICT nr_nova_prescricao_w, cd_medic_posicionar_w;

		if (coalesce(nr_nova_prescricao_w,0) > 0) then
			update	aval_pre_anestesica
			set	nr_seq_agenda = nr_seq_destino_p
			where	nr_seq_agenda = nr_seq_copia_p;
						
			update	conclusao_recom_apae
			set	nr_prescricao = nr_nova_prescricao_w
			where	nr_sequencia  = nr_sequencia_ww;
			commit;
			CALL Suspender_Prescricao(nr_prescricao_w,null,null,nm_usuario_p, 'N');
			ds_erro_w := Liberar_Prescricao(nr_nova_prescricao_w, nr_atendimento_w, eh_medico_w, obter_perfil_ativo, nm_usuario_p, 'S', ds_erro_w);
		end if;	
	end if;
end if;	
		
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE transf_prescr_apae_agenda ( nr_seq_copia_p bigint, nr_seq_destino_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

