SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE transf_prof_waiting_list ( schedule_sequence_p bigint, waiting_list_sequence_p bigint, ie_source_p text) AS $body$
DECLARE


									
nr_sequencia_w agenda_lista_espera_proc.nr_sequencia%type;						

professionals_schedule CURSOR FOR
SELECT 	*
from 	profissional_agenda
where 	nr_seq_agenda = schedule_sequence_p;

professionals_waiting_list CURSOR FOR
SELECT 	*
from 	agenda_lista_espera_prof
where 	nr_seq_lista = waiting_list_sequence_p;

BEGIN

if (schedule_sequence_p IS NOT NULL AND schedule_sequence_p::text <> '' AND waiting_list_sequence_p IS NOT NULL AND waiting_list_sequence_p::text <> '') then
	if (ie_source_p = 'S') then
		for professionals in professionals_schedule loop
			insert into agenda_lista_espera_prof(
				nr_sequencia,
				nr_seq_lista,
				dt_atualizacao,
				nm_usuario,
				cd_profissional,
				cd_funcao,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				ie_indicacao,
				nr_seq_equipe,
				cd_especialidade
			) values (
				nextval('agenda_lista_espera_prof_seq'),
				waiting_list_sequence_p,
				clock_timestamp(),
				wheb_usuario_pck.get_nm_usuario,
				professionals.cd_profissional,
				professionals.cd_funcao,
				professionals.dt_atualizacao_nrec,
				professionals.nm_usuario_nrec,
				professionals.ie_indicacao,
				professionals.nr_seq_equipe,
				professionals.cd_especialidade
			);
			
			commit;
		end loop;
	elsif (ie_source_p = 'W') then
		for professionals in professionals_waiting_list loop
			insert into profissional_agenda(
				nr_sequencia,
				nr_seq_agenda,
				dt_atualizacao,
				nm_usuario,
				cd_profissional,
				cd_funcao,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				ie_indicacao,
				nr_seq_equipe,
				cd_especialidade
			) values (
				nextval('profissional_agenda_seq'),
				schedule_sequence_p,
				clock_timestamp(),
				wheb_usuario_pck.get_nm_usuario,
				professionals.cd_profissional,
				professionals.cd_funcao,
				professionals.dt_atualizacao_nrec,
				professionals.nm_usuario_nrec,
				professionals.ie_indicacao,
				professionals.nr_seq_equipe,
				professionals.cd_especialidade
			);
			
			commit;
		end loop;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
