SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE transf_pront_set_itens (nr_seq_transf_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint ) AS $body$
DECLARE

 
cd_setor_destino_w	integer;
nr_sequencia_w		bigint;
cd_pessoa_destino_w	varchar(10);
ie_setor_same_w		varchar(1);
ie_gerar_devolucao_w	varchar(1);
ds_prontuario_w		varchar(2000);
ie_digitalizado_w	varchar(1);
nr_atendimento_w	bigint;
ds_parametro_w		varchar(10) := obter_valor_param_usuario(941,103,obter_perfil_ativo,nm_usuario_p,cd_estabelecimento_p);
ie_altera_status_w	varchar(1);
cd_classif_setor_w	varchar(2);

c01 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from same_prontuario a, 
		 transf_prontuario b, 
		 transf_prontuario_envelope c 
	where	a.nr_sequencia = c.nr_seq_prontuario 
	 and  b.nr_sequencia = c.nr_seq_transf 
	 and 	b.nr_sequencia = nr_seq_transf_p 
	 and	((ds_parametro_w = 'N') or (a.cd_estabelecimento = cd_estabelecimento_p));


BEGIN 
 
ie_altera_status_w	:= obter_valor_param_usuario(941,161,obter_perfil_ativo,nm_usuario_p,cd_estabelecimento_p);
 
/*select	nvl(vl_parametro,vl_parametro_padrao) 
into	ds_parametro_w 
from	funcao_parametro 
where	cd_funcao = 0 
and	nr_sequencia = 120;*/
 
 
select	max(a.cd_setor_destino), 
	max(a.cd_pessoa_destino), 
	max(ie_digitalizado), 
	max(nr_atendimento) 
into STRICT	cd_setor_destino_w, 
	cd_pessoa_destino_w, 
	ie_digitalizado_w, 
	nr_atendimento_w 
from	transf_prontuario a 
where	a.nr_sequencia = nr_seq_transf_p;
 
if (ie_digitalizado_w = 'S') then 
	update	same_prontuario 
	set	ie_digitalizado = 'S' 
	where	nr_atendimento = nr_atendimento_w 
	and	ie_digitalizado = 'N';
end if;
 
update	transf_prontuario 
set	dt_recebimento		= clock_timestamp(), 
	nm_usuario		= nm_usuario_p, 
	cd_pessoa_receb		= obter_pessoa_fisica_usuario(nm_usuario_p, 'C')	 
where	nr_sequencia	= nr_seq_transf_p;
 
update	same_prontuario a 
set	a.cd_setor_atendimento	= cd_setor_destino_w 
where a.nr_sequencia in (SELECT b.nr_seq_prontuario 
						 from 
						  transf_prontuario_envelope b 
						 where b.nr_seq_prontuario = a.nr_sequencia 
						  and b.nr_seq_transf = nr_seq_transf_p 
						) 
and	((ds_parametro_w = 'N') or (a.cd_estabelecimento = cd_estabelecimento_p));
 
if (ie_altera_status_w = 'S') then 
		 
	select	max(cd_classif_setor) 
	into STRICT	cd_classif_setor_w 
	from	setor_atendimento 
	where	cd_setor_atendimento = cd_setor_destino_w;
			 
	if (cd_classif_setor_w = '6') then 
		update	same_prontuario a 
		set	ie_status	= '1' 
		where	a.nr_sequencia in (SELECT b.nr_seq_prontuario 
					from	transf_prontuario_envelope b 
					where	b.nr_seq_prontuario = a.nr_sequencia 
					and	b.nr_seq_transf = nr_seq_transf_p) 
		and	((ds_parametro_w = 'N') or (a.cd_estabelecimento = cd_estabelecimento_p));
	else 
		update	same_prontuario a 
		set	ie_status	= '11' 
		where	a.nr_sequencia in (SELECT b.nr_seq_prontuario 
					from	transf_prontuario_envelope b 
					where	b.nr_seq_prontuario = a.nr_sequencia 
					and	b.nr_seq_transf = nr_seq_transf_p) 
		and	((ds_parametro_w = 'N') or (a.cd_estabelecimento = cd_estabelecimento_p));
	end if;
	 
end if;
 
select 	CASE WHEN max(cd_classif_setor)=6 THEN 'S'  ELSE 'N' END  
into STRICT 	ie_setor_same_w 
from 	setor_atendimento 
where 	cd_setor_atendimento = cd_setor_destino_w;
	 
select	coalesce(max(obter_valor_param_usuario(941,84, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p)), 'N') 
into STRICT	ie_gerar_devolucao_w
;
	 
open c01;
loop 
fetch c01 into 
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
begin 
insert	into	same_prontuario_hist( 
	nr_sequencia    , 
	nr_seq_same     , 
	dt_atualizacao   , 
	nm_usuario     , 
	nr_seq_operacao   , 
	dt_historico    , 
	nr_seq_caixa    , 
	nr_seq_local    , 
	cd_pessoa_fisica  , 
	dt_atualizacao_nrec , 
	nm_usuario_nrec	  , 
	cd_setor_solicitante) 
values (nextval('same_prontuario_hist_seq'), 
	nr_sequencia_w, 
	clock_timestamp(), 
	nm_usuario_p, 
	17, 
	clock_timestamp(), 
	null, 
	null, 
	cd_pessoa_destino_w, 
	clock_timestamp(), 
	nm_usuario_p, 
	cd_setor_destino_w 
);
 
ds_prontuario_w := nr_sequencia_w||','||ds_prontuario_w;
	 
end;
end loop;
close c01;
 
if (ie_gerar_devolucao_w = 'S') AND (ie_setor_same_w = 'S') THEN 
	CALL same_devolver_pront_transf(ds_prontuario_w, nm_usuario_p, cd_estabelecimento_p);
end if;
 
commit;
 
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
