SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE trans_assumir_transporte ( nm_usuario_p text, nr_sequencia_p bigint) AS $body$
DECLARE



cd_trasnportador_usuario_w	varchar(10);
ie_transportador_disp_w		varchar(1);



BEGIN

begin
select	a.cd_pessoa_fisica
into STRICT	cd_trasnportador_usuario_w
from	usuario a
where	upper(a.nm_usuario_pesquisa) = upper(nm_usuario_p);
exception
	when others then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(197128);
		--Pessoa física do usuário não encontrada. Atualize o cadastro do usuário
end;


select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_transportador_disp_w
from	transportador
where	cd_pessoa_fisica = cd_trasnportador_usuario_w
and	ie_situacao = 'A'
and	ie_status_recurso = 1;


if (ie_transportador_disp_w = 'S') then
	begin

	update	trans_solicitacao
	set	cd_transportador  = cd_trasnportador_usuario_w,
		ie_status	  = 3,
		nm_usuario	  = nm_usuario_p,
		dt_atualizacao	  = clock_timestamp()
	where	nr_sequencia	  = nr_sequencia_p;

	end;
else
	--Transportador não disponível ou inativo
	CALL wheb_mensagem_pck.exibir_mensagem_abort(197129);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
