CREATE OR REPLACE PROCEDURE trans_finalizar_intervalo ( nm_usuario_p text) AS $body$
DECLARE


cd_transportador_usuario_w	varchar(10);


BEGIN

begin
select	a.cd_pessoa_fisica
into STRICT	cd_transportador_usuario_w
from	usuario a
where	upper(a.nm_usuario_pesquisa) = upper(nm_usuario_p);
exception
	when others then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(197128);
		--Pessoa física do usuário não encontrada. Atualize o cadastro do usuário
end;

update	transportador
set	IE_STATUS_RECURSO = 1,
	IE_TIPO_INTERVALO  = NULL
where	cd_pessoa_fisica  = cd_transportador_usuario_w;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
