CREATE OR REPLACE PROCEDURE trans_liberar_solicitacao ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

ie_existe_w	smallint;

BEGIN

update	trans_solicitacao
set	dt_liberacao = clock_timestamp(),
	ie_status = 2,
	dt_atualizacao = clock_timestamp(),
	nm_usuario = nm_usuario_p
where	nr_sequencia = nr_sequencia_p;


begin
	select 	1
	into STRICT	ie_existe_w
	from 	trans_solicitacao
	where	nr_sequencia <> nr_sequencia_p
	and		coalesce(dt_solicitacao::text, '') = ''
	and		dt_atualizacao > clock_timestamp() - interval '60 days' LIMIT 1;

	update	trans_solicitacao
	set		dt_solicitacao = dt_atualizacao_nrec
	where	coalesce(dt_solicitacao::text, '') = '';

exception
when others then
	null;
end;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
