CREATE OR REPLACE PROCEDURE trans_retroceder_status ( nm_usuario_p text, nr_sequencia_p bigint) AS $body$
DECLARE



nr_status_transferencia_w	bigint;

BEGIN

select	coalesce(max(a.ie_status),1)
into STRICT	nr_status_transferencia_w
from	trans_historico a
where	a.nr_seq_solicitacao = nr_sequencia_p
and	a.nr_sequencia		= (	SELECT	max(b.nr_sequencia)
					from	trans_historico b
					where	b.nr_seq_solicitacao	= nr_sequencia_p
					and	b.nr_sequencia		< (select max(b.nr_sequencia)
									from trans_historico b
									where	b.nr_seq_solicitacao	= nr_sequencia_p));

CALL trans_alterar_status(nm_usuario_p,nr_sequencia_p,nr_status_transferencia_w);

commit;

end;
