CREATE OR REPLACE PROCEDURE tratar_baixa_cobr_titulo_inad ( nr_seq_tit_rec_cobr_p bigint, nr_titulo_p bigint, nr_seq_trans_inad_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ds_observacao_w			varchar(255);
ie_gerar_movto_bco_baixa_w	varchar(1);
vl_total_recebido_w		double precision := 0;
vl_despesa_bancaria_w		double precision;
vl_acrescimo_w			double precision;
vl_recebido_w			double precision;
nr_seq_baixa_w			bigint;
nr_seq_conta_banco_w		bigint;
cd_empresa_w			bigint;
nr_seq_cobr_escrit_w		bigint;
nr_seq_proj_rec_w		bigint;
nr_seq_trans_desp_banco_w	bigint;
nr_seq_trans_acres_banco_w	bigint;
cd_tipo_recebimento_w		integer;
dt_recebimento_w		timestamp;


BEGIN 
select	max(nr_sequencia) 
into STRICT	nr_seq_baixa_w 
from	titulo_receber_liq 
where	nr_titulo	= nr_titulo_p;
 
select	a.dt_recebimento, 
	c.nr_seq_trans_desp_banco, 
	c.nr_seq_trans_acres, 
	c.ie_gerar_movto_bco_baixa, 
	a.vl_recebido, 
	a.cd_tipo_recebimento, 
	a.nr_seq_conta_banco, 
	a.ds_observacao, 
	d.cd_empresa, 
	a.nr_seq_cobranca, 
	b.nr_seq_proj_rec 
into STRICT	dt_recebimento_w, 
	nr_seq_trans_desp_banco_w, 
	nr_seq_trans_acres_banco_w, 
	ie_gerar_movto_bco_baixa_w, 
	vl_recebido_w, 
	cd_tipo_recebimento_w, 
	nr_seq_conta_banco_w, 
	ds_observacao_w, 
	cd_empresa_w, 
	nr_seq_cobr_escrit_w, 
	nr_seq_proj_rec_w 
from	estabelecimento d, 
	parametro_contas_receber	c, 
	titulo_receber			b, 
	titulo_receber_liq		a 
where	b.nr_titulo		= a.nr_titulo 
and	b.cd_estabelecimento	= d.cd_estabelecimento 
and	b.cd_estabelecimento	= c.cd_estabelecimento 
and	a.nr_titulo		= nr_titulo_p 
and	a.nr_sequencia		= nr_seq_baixa_w 
and	coalesce(ie_lib_caixa, 'S') 	= 'S';
 
if (nr_seq_conta_banco_w IS NOT NULL AND nr_seq_conta_banco_w::text <> '') and (ie_gerar_movto_bco_baixa_w = 'S') then 
 
	vl_total_recebido_w	:= vl_recebido_w;
 
	if (coalesce(nr_seq_trans_desp_banco_w::text, '') = '') then 
		vl_total_recebido_w	:= vl_total_recebido_w + vl_despesa_bancaria_w; 	
	end if;
 
	if (coalesce(nr_seq_trans_acres_banco_w::text, '') = '') then 
		vl_total_recebido_w	:= vl_total_recebido_w + vl_acrescimo_w;
	end if;
	 
	insert into movto_trans_financ(nr_sequencia, 
		dt_transacao, 
		nr_seq_trans_financ, 
		vl_transacao, 
		dt_atualizacao, 
		nm_usuario, 
		nr_seq_titulo_receber, 
		dt_referencia_saldo, 
		nr_seq_banco, 
		cd_tipo_recebimento, 
		nr_lote_contabil, 
		ie_conciliacao, 
		ds_historico, 
		nr_seq_cobr_escrit, 
		nr_seq_proj_rec) 
	values (nextval('movto_trans_financ_seq'), 
		dt_recebimento_w, 
		nr_seq_trans_inad_p, 
		vl_total_recebido_w, 
		clock_timestamp(), 
		nm_usuario_p, 
		nr_titulo_p, 
		clock_timestamp(), 
		nr_seq_conta_banco_w, 
		cd_tipo_recebimento_w, 
		0, 
		'N', 
		ds_observacao_w, 
		nr_seq_cobr_escrit_w, 
		nr_seq_proj_rec_w);
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
