CREATE OR REPLACE PROCEDURE tratar_param_usuario_xml (cd_funcao_p bigint, nr_sequencia_p bigint, vl_parametro_p text, nm_usuario_p text) AS $body$
DECLARE

qt_reg_w	smallint;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	funcao_param_usuario
where	cd_funcao		= cd_funcao_p
and	nr_sequencia		= nr_sequencia_p
and	nm_usuario_param	= nm_usuario_p;

if (qt_reg_w > 0) then
	update	funcao_param_usuario
	set	vl_parametro		= vl_parametro_p
	where	cd_funcao		= cd_funcao_p
	and	nr_sequencia		= nr_sequencia_p
	and	nm_usuario_param	= nm_usuario_p;
else
	insert into funcao_param_usuario(
		cd_funcao,
		nr_sequencia,
		nm_usuario_param,
		dt_atualizacao,
		nm_usuario,
		vl_parametro,
		ds_observacao)
	values (cd_funcao_p,
		nr_sequencia_p,
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		vl_parametro_p,
		null);
end	if;

commit;

end	;
$body$
