CREATE OR REPLACE PROCEDURE tre_adicionar_dt_certificado ( nr_seq_evento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_inscrito_pre_w		bigint;

C01 CURSOR FOR
	SELECT	b.nr_sequencia
	from	tre_inscrito		a,
		tre_inscrito_presenca	b,
		tre_evento		c
	where	a.nr_sequencia = b.nr_seq_inscrito
	and	c.nr_sequencia = nr_seq_evento_p
	and	a.nr_seq_evento = c.nr_sequencia
	and	b.ie_presente = 'S';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_inscrito_pre_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	tre_inscrito_presenca
	set	dt_certificado = clock_timestamp()
	where	nr_sequencia = nr_seq_inscrito_pre_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
