CREATE OR REPLACE PROCEDURE tre_alerta_bloqueio_faltas ( cd_pessoa_fisica_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
nr_seq_evento_w	bigint;
qt_idade_w	bigint;

C01 CURSOR FOR 
	SELECT	nr_seq_evento 
	from	regra_envio_sms a, 
		ev_evento b 
	where	cd_estabelecimento	= cd_estabelecimento_p 
	and	ie_evento_disp = 'BL' 
	and	qt_idade_w between coalesce(qt_idade_min,0)	and coalesce(qt_idade_max,9999) 
	and	a.nr_seq_evento = b.nr_sequencia 
	and	coalesce(a.ie_situacao,'A') = 'A';	

BEGIN 
 
qt_idade_w	:= coalesce(obter_idade_pf(cd_pessoa_fisica_p,clock_timestamp(),'A'),0);
 
open C01;
loop 
fetch C01 into	 
	nr_seq_evento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL gerar_evento_paciente(nr_seq_evento_w,null,cd_pessoa_fisica_p,null,nm_usuario_p,null);
	end;
end loop;
close C01;
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
