SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE tre_consiste_candidatos (nr_seq_agenda_p bigint, ie_opcao_p bigint, nr_seq_modulo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_msg_p INOUT text) AS $body$
DECLARE


/* Ie_opcao_p:
1 - Consiste qtde min.
2 - Consiste qtde máx.
*/
qt_min_pessoas_w	integer;
qt_max_pessoas_w	integer;
qt_candidatos_w		integer:= 0;
qt_candidatos_ww	integer:= 0;
ds_msg_w		varchar(255):= '';
ie_opcao_w		smallint;
ie_forma_vaga_w		varchar(1);


C01 CURSOR FOR
	SELECT 	tre_obter_qtde_candidatos(nr_seq_agenda, nr_sequencia)
	from	tre_agenda_modulo
	where	nr_seq_agenda	= nr_seq_agenda_p;

BEGIN

ie_opcao_w	:= ie_opcao_p;

select	coalesce(max(obter_valor_param_usuario(7041, 10, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p)), 'M')
into STRICT	ie_forma_vaga_w
;

begin
select 	coalesce(qt_min_pessoas,0),
	coalesce(qt_max_pessoas,9999)
into STRICT	qt_min_pessoas_w,
	qt_max_pessoas_w
from 	tre_agenda
where	nr_sequencia = nr_seq_agenda_p;
exception
	when others then
		qt_min_pessoas_w:= 0;
		qt_max_pessoas_w:= 9999;
end;

if (ie_forma_vaga_w = 'A') or (ie_forma_vaga_w = 'C') then

	select 	count(distinct cd_pessoa_fisica)
	into STRICT	qt_candidatos_w
	from 	tre_candidato
	where	nr_seq_agenda = nr_seq_agenda_p;


	if (ie_opcao_p = 1) then

		if (qt_candidatos_w < qt_min_pessoas_w) then
			ds_msg_w:= wheb_mensagem_pck.get_texto(803115, 'QT_MIN_PESSOAS='||qt_min_pessoas_w);
		end if;

	elsif (ie_opcao_p = 2) then

		if (qt_candidatos_w + 1 > qt_max_pessoas_w) then
			ds_msg_w:= wheb_mensagem_pck.get_texto(803117, 'QT_MAX_PESSOAS='||qt_max_pessoas_w);
		end if;
	end if;

elsif (ie_forma_vaga_w = 'M') then
	begin
	/*  Alex August Schlote OS - 181332,  Deeverá consisitr pelos modulos da agenda*/

	select 	tre_obter_qtde_candidatos(nr_seq_agenda, nr_sequencia)
	into STRICT	qt_candidatos_w
	from	tre_agenda_modulo
	where	nr_seq_agenda	= nr_seq_agenda_p
	and	nr_sequencia	= nr_seq_modulo_p;
	exception
	when others then
		ie_opcao_w	:= 0;
	end;


	open C01;
	loop
	fetch C01 into
		qt_candidatos_ww;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
			if (ie_opcao_p = 1) then
				if (qt_candidatos_ww < qt_min_pessoas_w) then
				ds_msg_w:= wheb_mensagem_pck.get_texto(803115, 'QT_MIN_PESSOAS='||qt_min_pessoas_w);
				end if;
			elsif (ie_opcao_p = 2) then
				if (qt_candidatos_w + 1 > qt_max_pessoas_w) then
				ds_msg_w:= wheb_mensagem_pck.get_texto(803117, 'QT_MAX_PESSOAS='||qt_max_pessoas_w);
				end if;
			end if;
		end;
	end loop;
	close C01;
end if;

ds_msg_p:= ds_msg_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE tre_consiste_candidatos (nr_seq_agenda_p bigint, ie_opcao_p bigint, nr_seq_modulo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_msg_p INOUT text) FROM PUBLIC;

