CREATE OR REPLACE PROCEDURE tre_desfazer_desistencia ( nr_seq_inscrito_p bigint, nr_seq_motivo_p bigint, nm_usuario_p text, dt_encerramento_p timestamp, cd_estabelecimento_p bigint) AS $body$
DECLARE



nr_seq_evento_w			bigint;
cd_pessoa_fisica_w		varchar(10);
dt_inicio_w				timestamp;
dt_fim_w				timestamp;
dt_fim_real_w			timestamp;
nr_seq_inscrito_w		bigint;
ds_motivo_desistencia_w	varchar(90);
nr_seq_pac_dia_w		bigint;

BEGIN

	/* Tipos

	C - Candidato
	I - Inscrito

	*/
	update	tre_inscrito
	set	dt_desistencia			 = NULL,
		nr_seq_motivo_desistencia 	 = NULL,
		dt_atualizacao			= clock_timestamp(),
		nm_usuario			= nm_usuario_p
	where	nr_sequencia			= nr_seq_inscrito_p;

	select	max(ds_motivo_desistencia)
	into STRICT	ds_motivo_desistencia_w
	from	tre_motivo_desistencia
	where	nr_sequencia = nr_seq_motivo_p;

	CALL tre_retira_bloqueio_pac(nr_seq_inscrito_p, dt_encerramento_p,ds_motivo_desistencia_w, nm_usuario_p);

commit;

end;
