SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE tre_desfazer_evento (nr_seq_evento_p bigint) AS $body$
DECLARE

C01 CURSOR FOR
SELECT	nr_sequencia
from	tre_inscrito
where	nr_seq_evento = nr_seq_evento_p;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	tre_paciente_dia
	where	nr_seq_inscrito in (	SELECT	nr_sequencia
					from	tre_inscrito
					where	nr_seq_evento = nr_seq_evento_p);

nr_seq_inscrito_w	bigint;
nr_seq_pac_dia_w	bigint;
qt_reg_w		bigint;

BEGIN

select	count(*)
into STRICT	qt_reg_w
from	tre_inscrito_presenca a
where 	a.nr_seq_inscrito in (SELECT x.nr_sequencia
	from	tre_inscrito x
	where	x.nr_seq_evento = nr_seq_evento_p);

if (qt_reg_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(275892);
end if;

select	count(*)
into STRICT	qt_reg_w
from	tre_paciente_dia
where	nr_seq_inscrito in (SELECT	nr_sequencia
				from	tre_inscrito
				where	nr_seq_evento = nr_seq_evento_p)
and	ie_status_paciente <> 'N';

if (qt_reg_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(275893);
end if;



Open C01;
Loop
	Fetch C01 into	nr_seq_inscrito_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	delete from tre_inscrito_presenca where nr_seq_inscrito = nr_seq_inscrito_w;
	commit;
end loop;
Close C01;


open C02;
loop
fetch C02 into
	nr_seq_pac_dia_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	delete from tre_paciente_dia where nr_sequencia = nr_seq_pac_dia_w;
	end;
end loop;
close C02;

delete from tre_inscrito where nr_seq_evento = nr_seq_evento_p;
delete from tre_evento_modulo where nr_seq_evento = nr_seq_evento_p;
delete from tre_evento where nr_sequencia = nr_seq_evento_p;

commit;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
