CREATE OR REPLACE PROCEDURE tre_gerar_evento_avaliacao ( nr_seq_avaliacao_p bigint, nr_seq_evento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_inscrito_w	bigint;
nr_sequencia_w		bigint;
nr_seq_aval_w		bigint;
nr_seq_item_aval_w	bigint;
nr_sequencia_item_w	bigint;


c01 CURSOR FOR
SELECT	a.nr_sequencia
from	tre_inscrito a
where	a.nr_seq_evento = nr_seq_evento_p
and	not exists (	select	1
			from	tre_aval_eve_inscrito b
			where	b.nr_seq_inscrito = a.nr_sequencia);

c02 CURSOR FOR
SELECT	nr_sequencia
from	tre_aval_item
where	nr_seq_aval = nr_seq_aval_w
order by nr_seq_apres;


BEGIN
select	max(nr_seq_aval)
into STRICT	nr_seq_aval_w
from	tre_modulo_aval
where	nr_sequencia = nr_seq_avaliacao_p;


open C01;
loop
fetch C01 into
	nr_seq_inscrito_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	nextval('tre_aval_eve_inscrito_seq')
	into STRICT	nr_sequencia_w
	;

	insert into tre_aval_eve_inscrito(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_inscrito,
			nr_seq_aval_mod)
		values (nr_sequencia_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_inscrito_w,
			nr_seq_avaliacao_p);


	open C02;
	loop
	fetch C02 into
		nr_seq_item_aval_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		select	nextval('tre_aval_inscrito_item_seq')
		into STRICT	nr_sequencia_item_w
		;

		insert into tre_aval_inscrito_item(
				nr_sequencia,
				nr_seq_aval_inscrito,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_item_aval)
			values (nr_sequencia_item_w,
				nr_sequencia_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_item_aval_w);

		end;
	end loop;
	close C02;

	end;
end loop;
close C01;




commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
