SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE tre_gerenciar_liberacao (nr_sequencia_p bigint, ie_opcao_p text, ie_tipo_p text, nm_usuario_p text) AS $body$
DECLARE


/* ie_opcao_p	'L' - Liberar
		'D' - Desfazer Liberação
ie_tipo_p		'A' - Agenda
		'T' - Treinamento (Curso) */
ie_verifica_agenda_w	varchar(1);
cd_estabelecimento_w	smallint;
qt_candidato_w		bigint;
qt_turno_w		bigint;



BEGIN
if (ie_opcao_p = 'L') then
	if (ie_tipo_p = 'A') then
		select	cd_estabelecimento
		into STRICT	cd_estabelecimento_w
		from	tre_agenda
		where	nr_sequencia = nr_sequencia_p;

		select	count(*)
		into STRICT	qt_candidato_w
		from	tre_candidato
		where	nr_seq_agenda = nr_sequencia_p;

		select	count(*)
		into STRICT	qt_turno_w
		from	tre_agenda_turno
		where	nr_seq_agenda_tre = nr_sequencia_p;

		ie_verifica_agenda_w := Obter_Param_Usuario(7041, 21, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_verifica_agenda_w);

		if (ie_verifica_agenda_w = 'N') and
			((qt_candidato_w = 0) or (qt_turno_w = 0)) then
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(262011); -- Você não tem permissão para liberar uma agenda sem candidatos e turno! Favor verificar.
		end if;

		update	tre_agenda
		set 	dt_liberacao = clock_timestamp(),
			nm_usuario   = nm_usuario_p
		where   nr_sequencia = nr_sequencia_p;

	elsif (ie_tipo_p = 'T') then
		update	tre_curso
		set 	dt_liberacao = clock_timestamp(),
			nm_usuario   = nm_usuario_p
		where   nr_sequencia = nr_sequencia_p;
	end if;
elsif (ie_opcao_p = 'D') then
	if (ie_tipo_p = 'A') then
		update	tre_agenda
		set	dt_liberacao  = NULL,
			nm_usuario   = nm_usuario_p
		where 	nr_sequencia = nr_sequencia_p;
	elsif (ie_tipo_p = 'T') then
		update	tre_curso
		set	dt_liberacao  = NULL,
			nm_usuario   = nm_usuario_p
		where 	nr_sequencia = nr_sequencia_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
