CREATE OR REPLACE PROCEDURE tre_insere_falta_curso (nr_seq_inscrito_p bigint, dt_registro_p timestamp, ie_status_falta_p text, ds_motivo_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


qt_possui_cad_w		bigint;
nr_seq_pac_dia_w	bigint;

BEGIN
select	count(*)
into STRICT	qt_possui_cad_w
from	tre_paciente_dia
where	nr_seq_inscrito = nr_seq_inscrito_p
and	trunc(dt_registro) = trunc(dt_registro_p);

if (qt_possui_cad_w = 0) then
	nr_seq_pac_dia_w := tre_insere_pac_dia(nr_seq_inscrito_p, dt_registro_p, nm_usuario_p, nr_seq_pac_dia_w);
	update	tre_paciente_dia
	set	ie_status_paciente = ie_status_falta_p,
		ds_motivo_status   = ds_motivo_p
	where	nr_Sequencia = nr_seq_pac_dia_w;
else
	select	max(nr_Sequencia)
	into STRICT	nr_seq_pac_dia_w
	from	tre_paciente_dia
	where	nr_seq_inscrito = nr_seq_inscrito_p
	and	trunc(dt_registro) = trunc(dt_registro_p);

	update	tre_paciente_dia
	set	ie_status_paciente = ie_status_falta_p,
		ds_motivo_status   = ds_motivo_p
	where	nr_Sequencia = nr_seq_pac_dia_w;
end if;

if (ie_status_falta_p = 'I') then
	CALL tre_aplica_regra_falta(dt_registro_p,nr_seq_inscrito_p,nm_usuario_p,cd_estabelecimento_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
