CREATE OR REPLACE PROCEDURE tre_lib_alteracao_dia (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_acao_w		varchar(1);
dt_alteracao_w		timestamp;
nr_seq_agenda_w		bigint;

BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	update	tre_alteracao_dia_aula
	set	dt_liberacao = clock_timestamp(),
		nm_usuario_lib = nm_usuario_p
	where	nr_sequencia = nr_sequencia_p;

	select	ie_acao,
		dt_alteracao,
		nr_seq_agenda
	into STRICT	ie_acao_w,
		dt_alteracao_w,
		nr_seq_agenda_w
	from	tre_alteracao_dia_aula
	where	nr_sequencia = nr_sequencia_p;

	if (ie_acao_w = 'C') then
		CALL tre_cancelar_pac_dia(dt_alteracao_w,nr_seq_agenda_w,nm_usuario_p);
	elsif (ie_acao_w = 'A') then
		CALL tre_reposicao_pac_dia(dt_alteracao_w,nr_seq_agenda_w,nm_usuario_p);
	end if;

end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
