SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE tre_obter_curso_lista (ds_lista_curso_p text, ds_lista_agenda_curso_p INOUT text) AS $body$
DECLARE


ds_lista_Agenda_w	varchar(2000);
ie_pos_sep_w		smallint;
ie_pos_virgula_aux_w	smallint;
ie_pos_virgula_w	smallint;
tam_lista_w		bigint;
ds_lista_agenda_curso_w	varchar(2000);


BEGIN
IF (ds_lista_curso_p IS NOT NULL AND ds_lista_curso_p::text <> '') THEN
	ds_lista_Agenda_w	:= ds_lista_curso_p;

	WHILE	(ds_lista_Agenda_w IS NOT NULL AND ds_lista_Agenda_w::text <> '') LOOP
		begin
		tam_lista_w		:= LENGTH(ds_lista_Agenda_w);
		ie_pos_virgula_w	:= position(',' in ds_lista_Agenda_w);
		ie_pos_sep_w		:= position('-' in ds_lista_Agenda_w);

		IF (ie_pos_virgula_w <> 0) THEN
			ie_pos_virgula_aux_w	:= position(',' in SUBSTR(ds_lista_Agenda_w, ie_pos_sep_w + 1, tam_lista_w));
			IF (SUBSTR(ds_lista_Agenda_w, 1, (ie_pos_sep_w - 1))	= '15') THEN
				ds_lista_agenda_curso_w	:= ds_lista_agenda_curso_w || SUBSTR(ds_lista_Agenda_w, ie_pos_sep_w + 1, ie_pos_virgula_aux_w);
			end if;
		end if;

		ds_lista_Agenda_w	:= SUBSTR(ds_lista_Agenda_w, (ie_pos_virgula_w + 1), tam_lista_w);
		end;
	end loop;

end if;

ds_lista_agenda_curso_p := ds_lista_agenda_curso_w;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
