SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE tre_paciente_dia_faltou (nr_seq_inscrito_p bigint, dt_registro_p timestamp, nm_usuario_p text) AS $body$
DECLARE


qt_valida_inscrito_w	bigint;
qt_possui_cad_w		bigint;


BEGIN
select	count(*)
into STRICT	qt_valida_inscrito_w
from	tre_inscrito
where	nr_sequencia = nr_seq_inscrito_p;

if (qt_valida_inscrito_w > 0) then

	select	count(*)
	into STRICT	qt_possui_cad_w
	from	tre_paciente_dia
	where	nr_seq_inscrito = nr_seq_inscrito_p
	and	trunc(dt_registro) = trunc(dt_registro_p);

	if (qt_possui_cad_w = 0) then
		insert  into 	tre_paciente_dia(nr_sequencia,
				nr_seq_inscrito,
				dt_atualizacao,
				nm_usuario,
				ie_status,
				dt_registro)
			values (nextval('tre_paciente_dia_seq'),
				nr_seq_inscrito_p,
				clock_timestamp(),
				nm_usuario_p,
				'N',
				dt_registro_p);
	else
		update	tre_paciente_dia
		set	ie_status = 'N'
		where	nr_sequencia = (SELECT	max(nr_sequencia)
					from	tre_paciente_dia
					where	nr_seq_inscrito = nr_seq_inscrito_p
					and	trunc(dt_registro) = trunc(dt_registro_p));
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
