CREATE OR REPLACE PROCEDURE tre_reposicao_pac_dia (dt_registro_p timestamp, nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_pac_dia_w	bigint;
nr_seq_inscrito_w	bigint;
nr_seq_pac_dia_novo_w	bigint;

C01 CURSOR FOR
	SELECT  distinct  tre_obter_dados_pac_dia(b.nr_sequencia,dt_registro_p,'C'),
		b.nr_Sequencia
	from	tre_evento a,
		tre_inscrito b
	where	b.nr_seq_evento = a.nr_sequencia
	and	a.nr_seq_agenda = nr_seq_agenda_p;


BEGIN
open C01;
loop
fetch C01 into
	nr_seq_pac_dia_w,
	nr_seq_inscrito_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (nr_seq_pac_dia_w IS NOT NULL AND nr_seq_pac_dia_w::text <> '') then
		update	tre_paciente_dia
		set	ie_status_paciente = 'RE'
		where	nr_sequencia = nr_seq_pac_dia_w;
	else
		nr_seq_pac_dia_novo_w := tre_insere_pac_dia(nr_seq_inscrito_w, dt_registro_p, nm_usuario_p, nr_seq_pac_dia_novo_w);
		if (nr_seq_pac_dia_novo_w > 0) then
			update	tre_paciente_dia
			set	ie_status_paciente = 'RE'
			where	nr_sequencia = nr_seq_pac_dia_novo_w;
		end if;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
