CREATE OR REPLACE PROCEDURE tre_ret_status_pac_lista_esp ( nr_sequencia_p bigint, cd_pessoa_fisica_p text) AS $body$
DECLARE


nr_seq_curso_w		bigint;
cd_pessoa_fisica_w	varchar(10);

c01 CURSOR FOR
SELECT	a.nr_seq_curso,
	c.cd_pessoa_fisica
from	tre_agenda a,
	tre_candidato c
where	c.nr_seq_agenda = a.nr_sequencia
and     c.nr_seq_agenda = nr_sequencia_p
and	c.cd_pessoa_fisica = coalesce(cd_pessoa_fisica_p,c.cd_pessoa_fisica);


BEGIN

open c01;
loop	
fetch c01 into
	nr_seq_curso_w,
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	update	tre_pf_lista_espera
	set	ie_status 	= 'A'
	where	nr_seq_curso	= nr_seq_curso_w
	and	cd_pessoa_fisica = cd_pessoa_fisica_w;
end loop;
close c01;

commit;

end;
