CREATE OR REPLACE PROCEDURE trocar_codigo_convenio ( nr_sequencia_p bigint, ie_proc_mat_p text, cd_conversao_convenio_p text, nm_usuario_p text) AS $body$
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nr_sequencia_p > 0) then

	if (ie_proc_mat_p = 'M') then

		update	material_atend_paciente
		set	cd_material_convenio	= cd_conversao_convenio_p,
			dt_atualizacao 		= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			dt_conversao_manual	= clock_timestamp()
		where	nr_sequencia		= nr_sequencia_p;

	elsif (ie_proc_mat_p = 'P') then

		update	procedimento_paciente
		set	cd_procedimento_convenio= cd_conversao_convenio_p,
			dt_atualizacao 		= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			dt_conversao_manual	= clock_timestamp()
		where	nr_sequencia		= nr_sequencia_p;

	end if;

end if;

commit;

end;
$body$
