CREATE OR REPLACE PROCEDURE trocar_local_estoque_lote ( cd_local_estoque_p bigint, nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
BEGIN

update	prescr_mat_hor
set	cd_local_estoque = cd_local_estoque_p,
	nm_usuario	 =  nm_usuario_p,
	dt_atualizacao   = clock_timestamp()
where   nr_seq_lote	 = nr_seq_lote_p;

update	ap_lote
set	cd_local_estoque = cd_local_estoque_p,
	nm_usuario	 =  nm_usuario_p,
	dt_atualizacao   = clock_timestamp()
where      nr_sequencia = nr_seq_lote_p
and	ie_status_lote = 'G';


insert into ap_lote_historico(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		nr_seq_lote,
		ds_evento,
		ds_log)
	values (	nextval('ap_lote_historico_seq'),
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_lote_p,
		WHEB_MENSAGEM_PCK.get_texto(311241),
		WHEB_MENSAGEM_PCK.get_texto(311242,'DS_LOCAL_ESTOQUE=' || obter_desc_local_estoque(cd_local_estoque_p)));

commit;

end;
$body$
