SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE trocar_paciente_cirurgia (nr_cirurgia_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE


cont_w		integer;
nr_prescricao_w	bigint;


BEGIN

/* Daniel em 29/03/2007 OS 53683 */

SELECT	MAX(nr_prescricao)
INTO STRICT	nr_prescricao_w
FROM	cirurgia
WHERE	nr_cirurgia	=	nr_cirurgia_p;

BEGIN
SELECT	COUNT(*)
INTO STRICT	cont_w
FROM	prescr_medica
WHERE	(nr_atendimento IS NOT NULL AND nr_atendimento::text <> '')
AND	nr_prescricao = nr_prescricao_w;
EXCEPTION
WHEN OTHERS THEN
	cont_w	:= 0;
END;

IF (cont_w > 0) THEN
	CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(266297);
end if;

SELECT	COUNT(*)
INTO STRICT	cont_w
FROM	cirurgia
WHERE	nr_cirurgia		= nr_cirurgia_p
AND ((nr_atendimento IS NOT NULL AND nr_atendimento::text <> '')
	OR	ie_status_cirurgia	<> 1);

IF (cont_w > 0) THEN
	CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(266298);
end if;

SELECT	COUNT(*)
INTO STRICT	cont_w
FROM	agenda_paciente
WHERE	nr_cirurgia	= nr_cirurgia_p
AND	(nr_atendimento IS NOT NULL AND nr_atendimento::text <> '');

IF (cont_w > 0) THEN
	CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(266299);
end if;


UPDATE	prescr_medica
SET	cd_pessoa_fisica	= cd_pessoa_fisica_p,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
WHERE	nr_prescricao		= nr_prescricao_w;

UPDATE	cirurgia
SET	cd_pessoa_fisica 	= cd_pessoa_fisica_p,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
WHERE	nr_cirurgia		= nr_cirurgia_p;

UPDATE	agenda_paciente
SET	cd_pessoa_fisica	= cd_pessoa_fisica_p,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
WHERE	nr_cirurgia		= nr_cirurgia_p;

COMMIT;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
