SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE trocar_recurso_cronograma ( nr_seq_cronograma_p bigint, cd_pessoa_original_p text, cd_pessoa_nova_p text) AS $body$
DECLARE

 
nr_seq_etapa_cron_w		bigint;
nr_sequencia_w         bigint;
nr_seq_cliente_w        bigint;
										
c01 CURSOR FOR 
SELECT	nr_sequencia 
from	proj_cron_etapa 
where	nr_seq_cronograma = nr_seq_cronograma_p 
and	pr_etapa <> 100;

c02 CURSOR FOR 
SELECT	max(b.nr_seq_cliente), 
	max(b.nr_sequencia) 
from	proj_cronograma a, 
	proj_projeto b 
where	a.nr_seq_proj = b.nr_sequencia 
and	a.nr_sequencia = nr_seq_cronograma_p;

 

BEGIN 
open c02;
loop 
fetch c02 into 
	nr_seq_cliente_w, 
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c02 */
 
end loop;
close C02;
 
open C01;
loop 
fetch C01 into	 
	nr_seq_etapa_cron_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	 
	update	proj_cron_etapa_equipe 
	set	cd_programador	= cd_pessoa_nova_p 
	where	nr_seq_etapa_cron = nr_seq_etapa_cron_w 
	and	cd_programador	= cd_pessoa_original_p;
	 
end loop;
close C01;
 
	insert into com_cliente_hist(	 
		nr_sequencia, 
		nr_seq_cliente, 
		nr_seq_tipo, 
		dt_historico, 
		nm_usuario,		 
		dt_atualizacao, 
		ds_titulo, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		ds_historico, 
		nr_seq_projeto) 
	values (	nextval('com_cliente_hist_seq'), 
		nr_seq_cliente_w, 
		8, 
		clock_timestamp(), 
		wheb_usuario_pck.get_nm_usuario, 
		clock_timestamp(), 
		'Atividades internasxx', 
		clock_timestamp(), 
		wheb_usuario_pck.get_nm_usuario, 
		substr('Foi realizada a troca do recurso ' || OBTER_NOME_PESSOA_FISICA(cd_pessoa_original_p,'') || ' pelo recurso ' || OBTER_NOME_PESSOA_FISICA(cd_pessoa_nova_p,'') || '. Alteração realizada somente para as atividades não finalizadas.',1,4000), 
		nr_sequencia_w);
	 
	commit;
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
