SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE trocar_recurso_etapa ( nr_seq_etapa_cron_p bigint, cd_pessoa_original_p text, cd_pessoa_nova_p text) AS $body$
DECLARE

 
nr_sequencia_w         bigint;
nr_seq_cliente_w        bigint;
ds_historico_w					varchar(4000);	
	 
c02 CURSOR FOR 
SELECT	max(b.nr_seq_cliente), 
	max(b.nr_sequencia) 
from	proj_cronograma a, 
	proj_projeto b, 
	proj_cron_etapa c 
where	a.nr_seq_proj = b.nr_sequencia 
and	c.nr_sequencia = nr_seq_etapa_cron_p 
and 	a.nr_sequencia = c.nr_seq_cronograma;

 

BEGIN 
 
open c02;
loop 
fetch c02 into 
	nr_seq_cliente_w, 
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c02 */
 
end loop;
close C02;
 
 
	update	proj_cron_etapa_equipe 
	set	cd_programador	= cd_pessoa_nova_p 
	where	nr_seq_etapa_cron = nr_seq_etapa_cron_p 
	and	cd_programador	= cd_pessoa_original_p;
	 
	ds_historico_w := substr(wheb_mensagem_pck.get_texto(990915, 'RECURSO1=' || OBTER_NOME_PESSOA_FISICA(cd_pessoa_original_p,'') || ';RECURSO2=' || OBTER_NOME_PESSOA_FISICA(cd_pessoa_nova_p,'')),1,4000);
	 
	insert into com_cliente_hist(	 
		nr_sequencia, 
		nr_seq_cliente, 
		nr_seq_tipo, 
		dt_historico, 
		nm_usuario,		 
		dt_atualizacao, 
		ds_titulo, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		ds_historico, 
		nr_seq_projeto) 
	values (	nextval('com_cliente_hist_seq'), 
		nr_seq_cliente_w, 
		8, 
		clock_timestamp(), 
		wheb_usuario_pck.get_nm_usuario, 
		clock_timestamp(), 
		wheb_mensagem_pck.get_texto(990916), --Atividades realizadas 
		clock_timestamp(), 
		wheb_usuario_pck.get_nm_usuario, 
		ds_historico_w, 
		nr_sequencia_w);
	 
	commit;
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
