CREATE OR REPLACE PROCEDURE troca_marca_preco_cotacao ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_cot_item_forn_w		bigint;
vl_preco_w			double precision;
ds_marca_w			varchar(30);
vl_unitario_material_w		double precision;
ds_marca_fornec_w		varchar(30);


BEGIN

select	nr_seq_cot_item_forn,
	vl_preco,
	ds_marca
into STRICT	nr_seq_cot_item_forn_w,
	vl_preco_w,
	ds_marca_w
from	cot_compra_forn_item_op
where	nr_sequencia = nr_sequencia_p;

select	vl_unitario_material,
	ds_marca_fornec
into STRICT	vl_unitario_material_w,
	ds_marca_fornec_w
from	cot_compra_forn_item
where	nr_sequencia = nr_seq_cot_item_forn_w;

update	cot_compra_forn_item
set	vl_unitario_material		= vl_preco_w,
	vl_preco_liquido		= 0,
	vl_total_liquido_item		= 0,
	vl_presente		= 0,
	vl_unitario_liquido		= 0,
	ds_marca_fornec		= ds_marca_w,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_cot_item_forn_w;

update	cot_compra_forn_item_op
set	ds_marca	= ds_marca_fornec_w,
	vl_preco	= vl_unitario_material_w
where	nr_sequencia	= nr_sequencia_p;

commit;

end;
$body$
