CREATE OR REPLACE PROCEDURE tst_liberar_acao_defeito ( nm_usuario_p text, nr_sequencia_p bigint, ie_liberar_acao_p text) AS $body$
DECLARE


-- ie_liberar_acao_p (S - Liberar / N - Desfazer liberação
BEGIN
if (ie_liberar_acao_p = 'S') then
	begin
	update	tst_doc_acao_defeito
	set	dt_liberacao = clock_timestamp(),
		nm_usuario_lib = nm_usuario_p
	where	nr_sequencia = nr_sequencia_p;
	end;
else
	begin
	update	tst_doc_acao_defeito
	set	dt_liberacao  = NULL,
		nm_usuario_lib  = NULL
	where	nr_sequencia = nr_sequencia_p;
	end;
end if;

commit;

end;
