SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE tuss_carga_estrutura ( cd_capitulo_p bigint, cd_proc_p bigint, cd_grupo_p bigint, ds_capitulo_p text, ds_especialidade_p text, ds_grupo_p text, ds_erro_p INOUT text) AS $body$
DECLARE


qt_registros_w		bigint;
ds_erro_w		varchar(2000);
cd_capitulo_w		integer;
ds_capitulo_w		varchar(255);
cd_espec_w		bigint;
cd_grupo_w		bigint;


BEGIN

cd_capitulo_w		:= cd_capitulo_p;
ds_capitulo_w		:= substr(InitCap(ds_capitulo_p),1,40);
qt_registros_w	:= 0;
ds_erro_w		:= WHEB_MENSAGEM_PCK.get_texto(280298);

cd_espec_w		:= somente_numero('80' || substr(cd_proc_p,1,3));

if	((coalesce(ds_especialidade_p::text, '') = '') or (coalesce(ds_especialidade_p,'0') = '0')) then
	 cd_espec_w:= 80000;
end if;

/* Carga da area de procedimentos TUSS
  Reservado Ã¡rea 80 */
begin
select 	 count(*)
into STRICT	 qt_registros_w
from	 area_procedimento
where	 cd_area_procedimento	= cd_capitulo_w;
if (qt_registros_w	= 0) then
	 begin
	 insert into area_procedimento(
			 CD_AREA_PROCEDIMENTO ,
			 DS_AREA_PROCEDIMENTO ,
			 DT_ATUALIZACAO       ,
			 NM_USUARIO           ,
			 IE_ORIGEM_PROCED     ,
			 CD_ORIGINAL)
	 values (
			 cd_capitulo_p,
			 ds_capitulo_w,
			 clock_timestamp(),
			 WHEB_MENSAGEM_PCK.get_texto(280299),
			 8,
			 cd_capitulo_p);
	exception
  	when others then
    		ds_erro_w	:= SQLERRM(SQLSTATE);
    		/*insert into logxxxxxxx_tasy values(sysdate, 'TUSS', 80,
			 'Erro insert area : ' || to_char(cd_capitulo_w) || ds_erro_w);*/
	end;
end if;
end;


/* Carga da especialidade de procedimentos */

/*  Reservado especialidade 80000*/

begin
select 	count(*)
into STRICT	qt_registros_w
from	especialidade_proc
where	cd_especialidade	= cd_espec_w;
if (qt_registros_w	= 0) then
	 begin
	 insert into especialidade_proc(
			 CD_ESPECIALIDADE              ,
			 DS_ESPECIALIDADE              ,
			 DT_ATUALIZACAO                ,
			 NM_USUARIO                    ,
			 CD_AREA_PROCEDIMENTO          ,
			 CD_ESPECIALIDADE_MEDICA       ,
			 IE_ORIGEM_PROCED              ,
			 CD_ORIGINAL                   )
	 values (
			 cd_espec_w,
			 InitCap(substr(coalesce(ds_especialidade_p,WHEB_MENSAGEM_PCK.get_texto(280300)),1,240)),
			 clock_timestamp(),
			 WHEB_MENSAGEM_PCK.get_texto(280299),
			 cd_capitulo_w,
			 null,
			 8,
			 cd_espec_w);
	exception
  	when others then
    		ds_erro_w	:= SQLERRM(SQLSTATE);
    		/*insert into logxxxxx_tasy values(sysdate, 'TUSS', 80,
			 'Erro insert espec : ' || to_char(cd_espec_w) || ds_erro_w);*/
	end;
end if;
end;

/* Carga da grupo de procedimentos */

begin
if (coalesce(cd_grupo_p,0) <> 0) then
	cd_grupo_w		:= somente_numero('80' || cd_grupo_p);
	select count(*)
	into STRICT	 qt_registros_w
	from	 grupo_proc
	where	 cd_grupo_proc	= cd_grupo_w;
	if (qt_registros_w	= 0) then
		begin
		insert into grupo_proc(
				CD_GRUPO_PROC          ,
				DS_GRUPO_PROC          ,
				CD_ESPECIALIDADE       ,
				IE_SITUACAO            ,
				DT_ATUALIZACAO         ,
				NM_USUARIO             ,
				IE_ORIGEM_PROCED       ,
				CD_ORIGINAL            )
		values (
				cd_grupo_w,
				InitCap(substr(coalesce(ds_grupo_p,WHEB_MENSAGEM_PCK.get_texto(280301)),1,240)),
				cd_espec_w,
				'A',
				clock_timestamp(),
				WHEB_MENSAGEM_PCK.get_texto(280299),
				8,
				cd_grupo_w);
		exception
		when others then
			ds_erro_w	:= SQLERRM(SQLSTATE);
			/*insert into logxxxx_tasy values(sysdate, 'TUSS', 80,
				'Erro insert grupo : ' || to_char(cd_grupo_w) || ds_erro_w);*/
		end;
	end if;
else
	cd_grupo_w		:= somente_numero('80' || substr(cd_proc_p,1,5));
	select count(*)
	into STRICT	 qt_registros_w
	from	 grupo_proc
	where	 cd_grupo_proc	= cd_grupo_w;
	if (qt_registros_w	= 0) then
		begin
		insert into grupo_proc(
				CD_GRUPO_PROC          ,
				DS_GRUPO_PROC          ,
				CD_ESPECIALIDADE       ,
				IE_SITUACAO            ,
				DT_ATUALIZACAO         ,
				NM_USUARIO             ,
				IE_ORIGEM_PROCED       ,
				CD_ORIGINAL            )
		values (
				cd_grupo_w,
				InitCap(substr(coalesce(ds_grupo_p,WHEB_MENSAGEM_PCK.get_texto(280301)),1,240)),
				cd_espec_w,
				'A',
				clock_timestamp(),
				WHEB_MENSAGEM_PCK.get_texto(280299),
				8,
				cd_grupo_w);
		exception
		when others then
			ds_erro_w	:= SQLERRM(SQLSTATE);
			/*insert into logxxxxxx_tasy values(sysdate, 'TUSS', 80,
				'Erro insert grupo : ' || to_char(cd_grupo_w) || ds_erro_w);*/
		end;
	end if;
end if;
end;

commit;

ds_erro_p	:= ds_erro_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
