SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE tuss_carga_estrut_completa ( cd_proc_p bigint, ds_capitulo_p text, ds_especialidade_p text, ds_grupo_p text, ds_erro_p INOUT text) AS $body$
DECLARE


qt_registros_w		bigint;
ds_erro_w		varchar(2000);
cd_capitulo_w		integer;
ds_capitulo_w		varchar(255);
ds_especialidade_w	varchar(240);
cd_espec_w		bigint;
cd_grupo_w		bigint;
ds_grupo_w		varchar(240);


BEGIN

cd_capitulo_w		:= somente_numero('8' || substr(cd_proc_p,1,1));
ds_capitulo_w		:= substr(InitCap(ds_capitulo_p),1,40);
ds_especialidade_w	:= substr(InitCap(ds_especialidade_p),1,240);
ds_grupo_w		:= substr(InitCap(ds_grupo_p),1,240);
qt_registros_w		:= 0;
ds_erro_w		:= 'OK';
cd_espec_w		:= somente_numero(cd_capitulo_w || substr(cd_proc_p,1,3));
cd_grupo_w		:= somente_numero(cd_capitulo_w || substr(cd_proc_p,1,5));

/* Carga da area de procedimentos TUSS  */

begin
select 	 count(*)
into STRICT	 qt_registros_w
from	 area_procedimento
where	 cd_area_procedimento	= cd_capitulo_w;
if (qt_registros_w	= 0) then
	 begin
	 insert into area_procedimento(
			 CD_AREA_PROCEDIMENTO ,
			 DS_AREA_PROCEDIMENTO ,
			 DT_ATUALIZACAO       ,
			 NM_USUARIO           ,
			 IE_ORIGEM_PROCED     ,
			 CD_ORIGINAL)
	 values (
			 cd_capitulo_w,
			 ds_capitulo_w,
			 clock_timestamp(),
			 WHEB_MENSAGEM_PCK.get_texto(278198),
			 8,
			 cd_capitulo_w);
	exception
  	when others then
    		ds_erro_w	:= SQLERRM(SQLSTATE);
    		/*insert into logxxxxx_tasy values(sysdate, 'TUSS', 80,
			 'Erro insert area : ' || to_char(cd_capitulo_w) || ds_erro_w);*/
	end;
end if;
end;


/* Carga da especialidade de procedimentos */

begin
select 	count(*)
into STRICT	qt_registros_w
from	especialidade_proc
where	cd_especialidade	= cd_espec_w;
if (qt_registros_w	= 0) then
	 begin
	 insert into especialidade_proc(
			 CD_ESPECIALIDADE              ,
			 DS_ESPECIALIDADE              ,
			 DT_ATUALIZACAO                ,
			 NM_USUARIO                    ,
			 CD_AREA_PROCEDIMENTO          ,
			 CD_ESPECIALIDADE_MEDICA       ,
			 IE_ORIGEM_PROCED              ,
			 CD_ORIGINAL                   )
	 values (
			 cd_espec_w,
			 ds_especialidade_w,
			 clock_timestamp(),
			 WHEB_MENSAGEM_PCK.get_texto(278198),
			 cd_capitulo_w,
			 null,
			 8,
			 cd_espec_w);
	exception
  	when others then
    		ds_erro_w	:= SQLERRM(SQLSTATE);
    		/*insert into logxxxxx_tasy values(sysdate, 'TUSS', 80,
			 'Erro insert espec : ' || to_char(cd_espec_w) || ds_erro_w);*/
	end;
end if;
end;

/* Carga da grupo de procedimentos */

begin
select count(*)
into STRICT	 qt_registros_w
from	 grupo_proc
where	 cd_grupo_proc	= cd_grupo_w;

if (qt_registros_w	= 0) then
	begin
	insert into grupo_proc(
			CD_GRUPO_PROC          ,
			DS_GRUPO_PROC          ,
			CD_ESPECIALIDADE       ,
			IE_SITUACAO            ,
			DT_ATUALIZACAO         ,
			NM_USUARIO             ,
			IE_ORIGEM_PROCED       ,
			CD_ORIGINAL            )
	values (
			cd_grupo_w,
			ds_grupo_w,
			cd_espec_w,
			'A',
			clock_timestamp(),
			WHEB_MENSAGEM_PCK.get_texto(278198),
			8,
			cd_grupo_w);
	exception
	when others then
		ds_erro_w	:= SQLERRM(SQLSTATE);
		/*insert into logxxxxx_tasy values(sysdate, 'TUSS', 80,
			'Erro insert grupo : ' || to_char(cd_grupo_w) || ds_erro_w);*/
	end;
end if;
end;

commit;

ds_erro_p	:= ds_erro_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE tuss_carga_estrut_completa ( cd_proc_p bigint, ds_capitulo_p text, ds_especialidade_p text, ds_grupo_p text, ds_erro_p INOUT text) FROM PUBLIC;

