SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE tws_atualizar_mensalidade ( nr_titulo_p titulo_receber.nr_titulo%type, dt_vencimento_p timestamp, nm_usuario_p text, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, ds_retorno_regra_p INOUT text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Atualizar / recalcular a mensalidade do beneficiario ao gerar a 2. via do boleto.
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ x] Outros: TWS
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 

dt_vencimento_atual_w		timestamp;
cd_motivo_alteracao_w		alteracao_vencimento.cd_motivo%type	:= 0;
ie_atualiza_titulo_nao_venc_w	pls_web_param_geral.ie_atualiza_titulo_nao_venc%type;
ie_vencimento_prorrogado_w	varchar(1);


BEGIN

begin
	select	dt_vencimento
	into STRICT	dt_vencimento_atual_w
	from	titulo_receber
	where	nr_titulo	= nr_titulo_p;
exception
when others then
	dt_vencimento_atual_w	:= null;
end;

begin
	select	coalesce(ie_atualiza_titulo_nao_venc, 'N')
	into STRICT	ie_atualiza_titulo_nao_venc_w
	from	pls_web_param_geral
	where	cd_estabelecimento	= cd_estabelecimento_p;
exception
when others then
	ie_atualiza_titulo_nao_venc_w	:= null;
end;

--Atualizar titulo ao imprimir boleto nao vencido. 
if (trunc(clock_timestamp()) < dt_vencimento_atual_w) and (ie_atualiza_titulo_nao_venc_w = 'S') then
	CALL pls_alterar_carteira_cob_bol(nr_titulo_p, 'PO', nm_usuario_p);
end if;	

ie_vencimento_prorrogado_w	:= pls_verifica_dt_prorrogado(nr_titulo_p, dt_vencimento_p);	

if (ie_vencimento_prorrogado_w = 'S') then
	cd_motivo_alteracao_w	:= 1; --Prorrogacao de vencimento
else	
	cd_motivo_alteracao_w	:= 0;
end if;

--Rotina ja faz o commit
ds_retorno_regra_p := pls_atualizar_mensalidade_web(	nr_titulo_p, dt_vencimento_p, cd_motivo_alteracao_w, null, nm_usuario_p, null, ds_retorno_regra_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
