CREATE OR REPLACE PROCEDURE tx_alterar_status_encam (nr_seq_encaminhamento_p bigint, ie_status_p text, nm_usuario_p text) AS $body$
BEGIN
if (nr_seq_encaminhamento_p IS NOT NULL AND nr_seq_encaminhamento_p::text <> '') and (ie_status_p IS NOT NULL AND ie_status_p::text <> '') then
	update	tx_encaminhamento
	set	ie_status = ie_status_p
	where	nr_sequencia = nr_seq_encaminhamento_p;

	insert into tx_log_encaminhamento(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		ie_status,
		nr_seq_encaminhamento,
		dt_atualizacao_nrec,
		nm_usuario_nrec)
	values (	nextval('tx_log_encaminhamento_seq'),
		clock_timestamp(),
		nm_usuario_p,
		ie_status_p,
		nr_seq_encaminhamento_p,
		clock_timestamp(),
		nm_usuario_p);
end if;

commit;

end;
$body$
