CREATE OR REPLACE PROCEDURE tx_altera_trat_transplante ( nr_seq_tratamento_p bigint, nr_seq_receptor_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ie_tratamento_w			varchar(15);
nr_seq_tratamento_w		bigint;
ie_possui_transp_w		varchar(1);
nr_seq_motivo_fim_tratamento_w	bigint;
dt_tratamento_w			timestamp;
C01 CURSOR FOR
	SELECT	nr_sequencia
	into STRICT	nr_seq_tratamento_w
	from	paciente_tratamento
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	coalesce(nr_seq_motivo_fim::text, '') = ''
	and	ie_tratamento = 'TX';

BEGIN
select	max(ie_tratamento),
	max(dt_inicio_tratamento)
into STRICT	ie_tratamento_w,
	dt_tratamento_w
from	paciente_tratamento
where	nr_sequencia = nr_seq_tratamento_p;

select	max(nr_seq_motivo_fim_tratamento)
into STRICT	nr_seq_motivo_fim_tratamento_w
from	tx_parametros
where	cd_estabelecimento = cd_estabelecimento_p;

if (ie_tratamento_w IS NOT NULL AND ie_tratamento_w::text <> '') and (ie_tratamento_w = 'TR') then

	open C01;
	loop
	fetch C01 into
		nr_seq_tratamento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		update	paciente_tratamento
		set	dt_final_tratamento = dt_tratamento_w,
			nr_seq_motivo_fim = nr_seq_motivo_fim_tratamento_w
		where	nr_sequencia = nr_seq_tratamento_w;
		end;
	end loop;
	close C01;

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_possui_transp_w
	from	tx_receptor
	where	(dt_transplante IS NOT NULL AND dt_transplante::text <> '')
	and	nr_sequencia = nr_seq_receptor_p;

	if (ie_possui_transp_w = 'N') then
		update	tx_receptor
		set	dt_transplante = dt_tratamento_w
		where	nr_sequencia = nr_seq_receptor_p;
	end if;

end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
