SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE tx_inserir_medic_padrao ( nr_seq_receptor_p bigint, ds_lista_medic_p text, nm_usuario_p text, ie_tipo_p text) AS $body$
DECLARE


lista_informacao_w		varchar(1000);
ie_contador_w			bigint	:= 0;
tam_lista_w			bigint;
ie_pos_virgula_w		smallint;
nr_seq_medic_w			bigint;

/*
ie_tipo_p
0	- Intercorrência
1	- Imunosupressão
*/
BEGIN

lista_informacao_w	:= ds_lista_medic_p;

while((lista_informacao_w IS NOT NULL AND lista_informacao_w::text <> '') or ie_contador_w > 200) loop
	begin

	tam_lista_w			:= length(lista_informacao_w);
	ie_pos_virgula_w		:= position(',' in lista_informacao_w);

	/* Obter a sequência lida */

	if (ie_pos_virgula_w <> 0) then
		nr_seq_medic_w		:= substr(lista_informacao_w,1,(ie_pos_virgula_w - 1));
		lista_informacao_w	:= substr(lista_informacao_w,(ie_pos_virgula_w + 1),tam_lista_w);
	end if;

	insert into tx_medicamento(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_inicio,
		cd_material,
		qt_dose,
		cd_unidade_medida,
		nr_dias_uso,
		cd_intervalo,
		ie_via_aplicacao,
		nr_seq_receptor,
		ie_medic_intercorrencia,
		ie_medic_imunosupressao)
	SELECT	nextval('tx_medicamento_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		cd_material,
		qt_dose,
		cd_unidade_medida,
		nr_dias_uso,
		cd_intervalo,
		ie_via_aplicacao,
		nr_seq_receptor_p,
		CASE WHEN ie_tipo_p=0 THEN 'S'  ELSE 'N' END ,
		CASE WHEN ie_tipo_p=1 THEN IE_IMUNOSUPRESSAO  ELSE 'N' END
	from	tx_medicamento_padrao
	where	nr_sequencia	= nr_seq_medic_w;

	ie_contador_w	:= ie_contador_w + 1;

	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE tx_inserir_medic_padrao ( nr_seq_receptor_p bigint, ds_lista_medic_p text, nm_usuario_p text, ie_tipo_p text) FROM PUBLIC;

