SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE unificar_laudo_pacs_mmed ( ds_lista_exames_p text, nr_seq_interno_p bigint, nm_usuario_p text) AS $body$
DECLARE

				 
nr_sequencia_prescricao_w		bigint;
nr_prescricao_w			bigint;
nr_seq_prescr_proc_ditado_w		bigint;
ds_arquivo_w			varchar(80);
nr_seq_prproma_w		bigint;
nr_seq_laquite_w		bigint;
ds_lista_w			varchar(2000);
tam_lista_w			bigint;
ie_pos_virgula_w			smallint;
nr_seq_proc_pac_w		bigint;
nr_seq_interno_w			bigint;
qt_existe_virgula_w			bigint;
nr_prescr_w			bigint;
nr_sequencia_prescr_w		bigint;
nr_seq_prescr_proc_w		bigint;
nr_laudo_w			bigint;
ie_status_execucao_w		varchar(5);


BEGIN 
 
select	max(a.nr_prescricao), 
	max(a.nr_sequencia), 
	max(a.ie_status_execucao) 
into STRICT	nr_prescr_w, 
	nr_sequencia_prescr_w, 
	ie_status_execucao_w 
from	prescr_procedimento a 
where	a.nr_seq_interno = nr_seq_interno_p;	
 
select	max(a.nr_sequencia) 
into STRICT	nr_seq_prescr_proc_w 
from	procedimento_paciente a 
left join 	conta_paciente b 
on 	a.nr_interno_conta = b.nr_interno_conta 
where	coalesce(b.ie_cancelamento,'N') = 'N' 
and	a.nr_prescricao = nr_prescr_w 
and	a.nr_sequencia_prescricao = nr_sequencia_prescr_w;
 
begin 
 
select	nr_sequencia 
into STRICT	nr_laudo_w 
from	laudo_paciente 
where	nr_seq_proc = nr_seq_prescr_proc_w;
 
exception 
when others then 
	 
	begin 
	 
	select	nr_sequencia 
	into STRICT	nr_laudo_w 
	from	laudo_paciente 
	where	nr_prescricao = nr_prescr_w 
	and	nr_seq_prescricao = nr_sequencia_prescr_w;	
	 
	exception 
	when others then 
		nr_laudo_w := 0;
	end;
	 
end;
 
if (nr_laudo_w = 0) then 
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(260086);
end if;
 
ds_lista_w := ds_lista_exames_p;
	 
select	position(',' in ds_lista_w) 
into STRICT	qt_existe_virgula_w
;
 
if (qt_existe_virgula_w = 0) then 
	ds_lista_w := ds_lista_w || ',';
end if;	
 
if (ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') then 
	begin 
  
 begin  
   select nr_sequencia, ds_arquivo, nr_seq_laquite, nr_seq_prproma 
   into STRICT nr_seq_prescr_proc_ditado_w, ds_arquivo_w, nr_seq_laquite_w, nr_seq_prproma_w 
   from prescr_proc_ditado 
   where nr_seq_prescr_proc = nr_seq_interno_p;
 exception when no_data_found then 
   nr_seq_prescr_proc_ditado_w := null;
 end;
 
	while(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') loop 
		begin 
		 
		tam_lista_w := length(ds_lista_w);
		ie_pos_virgula_w := position(',' in ds_lista_w);
	 
		if (ie_pos_virgula_w <> 0) then 
			nr_seq_interno_w := (substr(ds_lista_w,1,(ie_pos_virgula_w - 1)))::numeric;
			ds_lista_w := substr(ds_lista_w,(ie_pos_virgula_w + 1),tam_lista_w);
		end if;
   
		select	max(a.nr_prescricao), 
			max(a.nr_sequencia) 
		into STRICT	nr_prescricao_w, 
			nr_sequencia_prescricao_w 
		from	prescr_procedimento a 
		where	a.nr_seq_interno = nr_seq_interno_w;		
		 
		update	procedimento_paciente 
		set	nr_laudo = nr_laudo_w, 
			nr_controle = CASE WHEN Obter_sigla_Exame_interno(nr_seq_proc_interno) = NULL THEN null  ELSE SUBSTR(Obter_sigla_Exame_interno(nr_seq_proc_interno)||nr_interno_conta||nr_sequencia,1,254) END  
		where	nr_prescricao = nr_prescricao_w 
		and	nr_sequencia_prescricao = nr_sequencia_prescricao_w;
		 
		update	prescr_procedimento 
		set	ie_status_execucao = ie_status_execucao_w 
		where	nr_prescricao = nr_prescricao_w 
		and	nr_sequencia = nr_sequencia_prescricao_w;
 
		if (nr_seq_prescr_proc_ditado_w IS NOT NULL AND nr_seq_prescr_proc_ditado_w::text <> '') then 
			insert into prescr_proc_ditado(nr_sequencia, nr_seq_prescr_proc, dt_atualizacao, nm_usuario, ds_arquivo, nr_seq_laquite, nr_seq_prproma) 
			values (nextval('prescr_proc_ditado_seq'), nr_seq_interno_w, clock_timestamp(), nm_usuario_p, ds_arquivo_w, nr_seq_laquite_w, nr_seq_prproma_w);
		end if;
 
		commit;
					 
		CALL gravar_auditoria_mmed(nr_prescricao_w,nr_sequencia_prescricao_w,nm_usuario_p,23,null);
		 
		end;	
	end loop;	
  
 CALL unificar_captura_img(nr_laudo_w, nm_usuario_p);
  
	end;	
end if;	
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
