CREATE OR REPLACE PROCEDURE unlink_canceled_enc_schedule ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


						
dt_cancelamento_w	timestamp;


BEGIN

select	max(a.dt_cancelamento)
into STRICT	dt_cancelamento_w
from	atendimento_paciente a,
	AGENDA_PACIENTE b
where	a.nr_atendimento = b.nr_atendimento
and	b.nr_sequencia	= nr_sequencia_p;

if (dt_cancelamento_w IS NOT NULL AND dt_cancelamento_w::text <> '') then

	CALL vinc_desv_atendimento_agenda(	nr_sequencia_p,
					null,
					nm_usuario_p,
					'D');
end if;

commit;

end;
