SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE unlink_nr_prescr_mipres ( nr_sequencia_p PRESCR_MIPRES.NR_SEQUENCIA%TYPE, ds_justificativa_p PRESCR_MIPRES.DS_JUSTIFICATIVA%TYPE) AS $body$
DECLARE


nm_usuario_w                PRESCR_MIPRES.NM_USUARIO%TYPE;
nr_prescricao_w             PRESCR_MIPRES.NR_PRESCRICAO%TYPE;
nr_atendimento_w            PRESCR_MIPRES.NR_ATENDIMENTO%TYPE;


BEGIN

  nm_usuario_w := wheb_usuario_pck.get_nm_usuario;

  UPDATE PRESCR_MIPRES a SET
    a.nr_presc_mipres    = NULL,
    a.dt_atualizacao    = clock_timestamp(),
    a.nm_usuario        = nm_usuario_w,
    a.ie_status         = 1,
    a.ds_justificativa  = ds_justificativa_p
  WHERE a.nr_sequencia  = nr_sequencia_p;

  SELECT max(a.NR_PRESCRICAO),
         max(a.NR_ATENDIMENTO)
  INTO STRICT   nr_prescricao_w,
         nr_atendimento_w
  FROM   PRESCR_MIPRES a
  WHERE  a.nr_sequencia = nr_sequencia_p;

  CALL SET_TASK_LIST_MIPRES('I',
      nr_atendimento_w,
      nr_prescricao_w,
      nr_sequencia_p,
      nm_usuario_w);

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
