CREATE OR REPLACE PROCEDURE update_backup_captura (ds_backup_p text, nr_sequencia_p bigint) AS $body$
DECLARE

ds_caminho_backup_old_w varchar(4000);

nr_seq_log_atual_w bigint;
ie_log_w		   varchar(10);


BEGIN
  SELECT ds_caminho_backup
  INTO STRICT   ds_caminho_backup_old_w
  FROM   captura_imagem
  WHERE  nr_sequencia  = nr_sequencia_p;


  SELECT * FROM gravar_log_alteracao(ds_caminho_backup_old_w, ds_backup_p, obter_usuario_ativo, nr_seq_log_atual_w, 'DS_CAMINHO_BACKUP', ie_log_w, NULL, 'CAPTURA_IMAGEM', nr_sequencia_p, NULL) INTO STRICT nr_seq_log_atual_w, ie_log_w;

  UPDATE captura_imagem
  SET    ds_caminho_backup = ds_backup_p
  WHERE  nr_sequencia  = nr_sequencia_p;

  COMMIT;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
